                                                if (sqlACTIONS("UPDATE", "dvi_itinerary_route_hotspot_details", $route_hotspot_refresh_time_arrFields, $route_hotspot_refresh_time_arrValues, $route_hotspot_refresh_time_sqlwhere)) :
