                                                // Get the numeric representation of the day of the week (0 for Sunday, 1 for Monday, etc.)
                                                $dayOfWeekNumeric = (int)date('N', $timestamp) - 1;
                                            endif;

                                            $location_name = getSTOREDLOCATIONDETAILS($start_location_id, 'SOURCE_LOCATION');

