                            endif;

                            if ($hotspot_start_time >= $route_end_time) :
                                $itinerary_route_details_arrFields = array('`route_end_time`');
                                $itinerary_route_details_arrValues = array("$hotspot_start_time");
                                $itinerary_route_details_sqlwhere = " `itinerary_plan_ID` = '$itinerary_plan_id' AND `itinerary_route_ID` = '$itinerary_route_id' ";

                                //UPDATE ITINEARY ROUTE AND PLAN DETAILS
                                if (sqlACTIONS("UPDATE", "dvi_itinerary_route_details", $itinerary_route_details_arrFields, $itinerary_route_details_arrValues, $itinerary_route_details_sqlwhere)) :
                                endif;

                            endif;
                        endif;
                    endif;
                endforeach;
            endif;
        endif;

        echo json_encode($response);

    elseif ($_GET['type'] == 'itineary_basic_info') :

        $response = [];
        $errors = [];

        if (empty($_POST['arrival_location'])) :
            $errors['arrival_location_required'] = true;
        endif;
        if (empty($_POST['departure_location'])) :
            $errors['departure_location_required'] = true;
        endif;
        if (empty($_POST['trip_start_date'])) :
            $errors['trip_start_date_required'] = true;
        endif;
        if (empty($_POST['trip_end_date'])) :
            $errors['trip_end_date_required'] = true;
        endif;
         if (empty($_POST['trip_start_time'])) :
            $errors['trip_start_time_required'] = true;
        endif;
        if (empty($_POST['trip_end_time'])) :
            $errors['trip_end_time_required'] = true;
        endif;
        if (empty($_POST['expecting_budget'])) :
            $errors['expecting_budget_required'] = true;
        endif;

        if ($logged_user_level == 1 && $logged_user_level == 3) :
            //ADMIN AND TRAVEL EXPERT
            if (empty($_POST['agent'])) :
                $errors['agent_required'] = true;
            endif;
        endif;

        if (!empty($errors)) :
            //error call
            $response['success'] = false;
            $response['errors'] = $errors;
        else :
