                                                                    // **INSERT THE ITINERARY VIA ROUTE TRAVELING DATA**
                                                                    if (sqlACTIONS("INSERT", "dvi_itinerary_route_hotspot_details", $via_route_traveling_arrFields, $via_route_traveling_arrValues, '')) :
