        <script>
            function confirmITINEARYHOTSPOTDELETE(itinerary_plan_ID, itinerary_route_ID, hotspot_ID) {
                $.ajax({
                    type: 'post',
                    url: 'engine/ajax/ajax_latest_manage_itineary.php?type=confirm_delete_itinerary_route_hotspot',
                    data: {
                        itinerary_plan_ID: itinerary_plan_ID,
                        itinerary_route_ID: itinerary_route_ID,
                        hotspot_ID: hotspot_ID,
                    },
                    dataType: 'json',
                    success: function(response) {
                        if (!response.success) {
                            // NOT SUCCESS RESPONSE
                            if (response.errors.something_went_wrong) {
                                TOAST_NOTIFICATION('warning', response.errors.something_went_wrong, 'Warning !!!', '', '', '', '', '', '', '', '', '');
                            }
                        } else {
                            // SUCCESS RESPONSE
                            TOAST_NOTIFICATION('success', 'Successfully Hotspot Deleted !!!', 'Success !!!', '', '', '', '', '', '', '', '', '');
                            $('#close_hotspot_delete').click();       
                            showDAYWISEHOTSPOT_DETAILS(itinerary_route_ID, itinerary_plan_ID, '<?= $group_type; ?>');
