            endif;
            /* $hotspot_order = 0; */

            #RETRIVE HOTSPOT DATA BASED ON THE LOCATION LOCATION NAME AND VIA ROUTE
            $select_hotspot_details_data = sqlQUERY_LABEL("SELECT HOTSPOT_PLACE.`hotspot_ID`, HOTSPOT_PLACE.`hotspot_name`, HOTSPOT_PLACE.`hotspot_description`, HOTSPOT_PLACE.`hotspot_address`, HOTSPOT_PLACE.`hotspot_location`, HOTSPOT_PLACE.`hotspot_latitude`, HOTSPOT_PLACE.`hotspot_longitude`, HOTSPOT_PLACE.`hotspot_duration`,HOTSPOT_PLACE.`hotspot_priority` FROM `dvi_hotspot_place` HOTSPOT_PLACE LEFT JOIN `dvi_hotspot_timing` HOTSPOT_TIMING ON HOTSPOT_TIMING.`hotspot_ID` = HOTSPOT_PLACE.`hotspot_ID` WHERE HOTSPOT_PLACE.`deleted` = '0' AND HOTSPOT_PLACE.`status` = '1' AND HOTSPOT_TIMING.`hotspot_timing_day` = '$dayOfWeekNumeric' AND HOTSPOT_PLACE.`hotspot_ID` = '$requested_hotspot_ID' ORDER BY CASE WHEN HOTSPOT_PLACE.`hotspot_priority` = 0 THEN 1 ELSE 0 END, HOTSPOT_PLACE.`hotspot_priority` ASC") or die("#1-UNABLE_TO_COLLECT_HOTSPOT_PLACE_LIST:" . sqlERROR_LABEL());
            $select_hotspot_details_num_rows_count = sqlNUMOFROW_LABEL($select_hotspot_details_data);

