                    $posB = array_search($b['hotspot_ID'], $order_by_location);

                    // Compare the positions
                    return $posA - $posB; // Ascending order
                });

                $hotspot_eligibility_result = check_ITINEARY_HOTSPOT_CONFLICT($hotspot_details, $requested_hotspot_ID,$hotspot_siteseeing_travel_start_time, $staring_location_latitude, $staring_location_longtitude, $max_route_end_time, $dayOfWeekNumeric,$order_by_location);
                        
