        else:
            //LOG THE REASON WHY THE HOTSPOT WAS NOT ADDED
            $dateTIME = date('d/m/Y H:i:s');
            $reason = $insident_on . ' ' . $exceeds_route_start_time ? "Exceeds route start time" : "Exceeds route end time";
            error_log("itinerary_plan_ID => $itinerary_plan_ID | itinerary_route_ID => $itinerary_route_ID Hotspot $next_hotspot_name (ID: $hotspot_ID) was not added to the itinerary. Reason: $reason - $dateTIME\n\n", 3, $error_log_file);
            #$errors['exceeds_route_end_time'] = "Sorry, your route end time almost reached.";
            $errors['hotspot_operating_hours_not_available'] = "Sorry, $next_hotspot_name operates during the following hours: <br>$get_hotspot_operating_hours<br><br>Total Spending Duration: $total_spend_duration";
        endif;

        if (!empty($errors)) :
            //error call
