                // Assign order number based on hotspot ID
                $hotspot_order++;
                //ASSIGN THE HOTSPOT ORDERS
                $route_hotspot_traveling_arrFields = array(
                    '`itinerary_plan_ID`', '`itinerary_route_ID`', '`item_type`', '`hotspot_order`', '`hotspot_ID`',
