                );
                $route_hotspot_traveling_arrValues = array(
                    "$itinerary_plan_ID", "$itinerary_route_ID", "3", "$hotspot_order", "$hotspot_ID",
                    "1", "$duration_formatted", "$hotspot_travelling_distance", "$hotspot_siteseeing_travel_start_time", "$hotspot_siteseeing_travel_end_time", "$logged_user_id", "1"
                );

