                    $hotspot_duration_seconds = strtotime("1970-01-01 $next_hotspot_duration UTC");
                    $hotspot_siteseeing_new_end_time = strtotime($hotspot_siteseeing_start_time) + $hotspot_duration_seconds;

                    //CONVERT THE NEW TIME TO {hotspot_siteseeing_end_time} H:i:s FORMAT
                    $hotspot_siteseeing_end_time = date('H:i:s', $hotspot_siteseeing_new_end_time);

                    $route_hotspot_arrFields = array('`itinerary_plan_ID`', '`itinerary_route_ID`', '`item_type`', '`hotspot_order`', '`hotspot_ID`', '`hotspot_adult_entry_cost`', '`hotspot_child_entry_cost`', '`hotspot_infant_entry_cost`', '`hotspot_foreign_adult_entry_cost`', '`hotspot_foreign_child_entry_cost`', '`hotspot_foreign_infant_entry_cost`', '`hotspot_amout`', '`hotspot_traveling_time`', '`hotspot_start_time`', '`hotspot_end_time`', '`createdby`', '`status`');
                    $route_hotspot_arrValues = array("$itinerary_plan_ID", "$itinerary_route_ID", "4", "$hotspot_order", "$hotspot_ID", "$hotspot_adult_entry_cost", "$hotspot_child_entry_cost", "$hotspot_infant_entry_cost", "$hotspot_foreign_adult_entry_cost", "$hotspot_foreign_child_entry_cost", "$hotspot_foreign_infant_entry_cost", "$hotspot_amout", "$next_hotspot_duration", "$hotspot_siteseeing_start_time", "$hotspot_siteseeing_end_time", "$logged_user_id", "1");

                    $check_hotspot_siteseeing_already_added_the_itineary_plan = sqlQUERY_LABEL("SELECT `route_hotspot_ID` FROM `dvi_itinerary_route_hotspot_details` WHERE `deleted` = '0' and `status` = '1' and `itinerary_plan_ID` = '$itinerary_plan_ID' AND `itinerary_route_ID` = '$itinerary_route_ID' and `hotspot_ID` = '$hotspot_ID' and `item_type`='4'") or die("#1-UNABLE_TO_COLLECT_ITINEARY_HOTSPOT_LIST:" . sqlERROR_LABEL());
                    $check_hotspot_siteseeing_already_added_num_rows_count = sqlNUMOFROW_LABEL($check_hotspot_siteseeing_already_added_the_itineary_plan);

                    if ($check_hotspot_siteseeing_already_added_num_rows_count == 0) :
                        //INSERT THE ITINEARY HOTSPOT SITE SEEING PLACE DATA
                        if (sqlACTIONS("INSERT", "dvi_itinerary_route_hotspot_details", $route_hotspot_arrFields, $route_hotspot_arrValues, '')) :

                            # INSERT THE TRAVELLER WISE HOTSPOT ENTRY CHARGES
                            if ($entry_ticket_required == 1) :
                                $route_hotspot_ID = sqlINSERTID_LABEL();
                                
                                # COLLECT ALL THE TRAVELLER DETAILS
                                $traveller_data = [
                                    'adult' => [
                                        'count' => get_ITINEARY_PLAN_DETAILS($itinerary_plan_ID, 'total_adult'),
                                        'type' => 1,
