        // Initialize the array to store existing hotspot IDs
        $array_of_existing_hotspot_ID = [];
        $select_existing_itineary_hotspot_details = sqlQUERY_LABEL("SELECT `hotspot_ID` FROM `dvi_itinerary_route_hotspot_details` WHERE `deleted` = '0' and `status` = '1' AND `itinerary_plan_ID` = '$itinerary_plan_ID' and `itinerary_route_ID` = '$itinerary_route_ID'") or die("#1-UNABLE_TO_COLLECT_ITINEARY_HOTSPOT_LIST:" . sqlERROR_LABEL());
        while ($fetch_existing_hotspot_data = sqlFETCHARRAY_LABEL($select_existing_itineary_hotspot_details)) :
            $array_of_existing_hotspot_ID[] = $fetch_existing_hotspot_data['hotspot_ID'];
        endwhile;

        // Add the new hotspot ID to the existing array
        $array_of_existing_hotspot_ID[] = $hotspot_ID;

        // Remove zeros from the array
        $array_of_existing_hotspot_ID = array_filter($array_of_existing_hotspot_ID);

        // Remove duplicate values from the array
        $array_of_existing_hotspot_ID = array_unique($array_of_existing_hotspot_ID);

        $array_of_existing_hotspot_ID = array_values($array_of_existing_hotspot_ID);

        $comma_seperated_hotspot_ID = implode(',', $array_of_existing_hotspot_ID);

        // Convert the date string to a Unix timestamp using strtotime
        $timestamp = strtotime($itinerary_route_date);

        if ($timestamp !== false) :
            // Get the numeric representation of the day of the week (0 for Sunday, 1 for Monday, etc.)
            $dayOfWeekNumeric = (int)date('N', $timestamp) - 1;
        endif;
        
        #RETRIVE HOTSPOT DATA BASED ON THE LOCATION LOCATION NAME AND VIA ROUTE
        $select_hotspot_details_data = sqlQUERY_LABEL("SELECT HOTSPOT_PLACE.`hotspot_ID`, HOTSPOT_PLACE.`hotspot_name`, HOTSPOT_PLACE.`hotspot_description`, HOTSPOT_PLACE.`hotspot_address`, HOTSPOT_PLACE.`hotspot_location`, HOTSPOT_PLACE.`hotspot_latitude`, HOTSPOT_PLACE.`hotspot_longitude`, HOTSPOT_PLACE.`hotspot_duration`, HOTSPOT_PLACE.`hotspot_priority` FROM `dvi_hotspot_place` HOTSPOT_PLACE LEFT JOIN `dvi_hotspot_timing` HOTSPOT_TIMING ON HOTSPOT_TIMING.`hotspot_ID` = HOTSPOT_PLACE.`hotspot_ID` WHERE HOTSPOT_PLACE.`deleted` = '0' AND HOTSPOT_PLACE.`status` = '1' AND HOTSPOT_PLACE.`hotspot_ID` IN ($comma_seperated_hotspot_ID) AND HOTSPOT_TIMING.`hotspot_timing_day` = '$dayOfWeekNumeric' GROUP BY HOTSPOT_PLACE.`hotspot_ID`") or die("#1-UNABLE_TO_COLLECT_HOTSPOT_PLACE_LIST:" . sqlERROR_LABEL());
        $select_hotspot_details_num_rows_count = sqlNUMOFROW_LABEL($select_hotspot_details_data);

        // Initialize variables for the starting location
        $previous_hotspot_location = $get_staring_location_name;
        $staring_location_latitude = $get_staring_location_latitude;
        $staring_location_longtitude = $get_staring_location_longtitude;

        if ($select_hotspot_details_num_rows_count > 0): 
            while ($fetch_hotspot_data = sqlFETCHARRAY_LABEL($select_hotspot_details_data)): 
                // Proceed with adding the hotspot to the itinerary for the current day
                $hotspot_ID = $fetch_hotspot_data['hotspot_ID'];
                $hotspot_name = $fetch_hotspot_data['hotspot_name'];
                $hotspot_description = $fetch_hotspot_data['hotspot_description'];
                $hotspot_address = $fetch_hotspot_data['hotspot_address'];
                $hotspot_location = $fetch_hotspot_data['hotspot_location'];
                $hotspot_latitude = $fetch_hotspot_data['hotspot_latitude'];
                $hotspot_longitude = $fetch_hotspot_data['hotspot_longitude'];
                $hotspot_duration = $fetch_hotspot_data['hotspot_duration'];
                $hotspot_priority = $fetch_hotspot_data['hotspot_priority'];

                // Determine the travel location type
                $travel_location_type = getTravelLocationType($previous_hotspot_location, $hotspot_location);

                // Calculate the distance and duration from the starting location
                $result = calculateDistanceAndDuration($staring_location_latitude, $staring_location_longtitude, $hotspot_latitude, $hotspot_longitude, $travel_location_type);
                $get_hotspot_travelling_distance = number_format($result['distance'], 2, '.', '');

                // Categorize hotspots based on location type
                $hotspot_details[] = [
                    'hotspot_ID' => $hotspot_ID,
                    'hotspot_siteseeing_travel_start_time' => $hotspot_siteseeing_travel_start_time,
                    'hotspot_name' => $hotspot_name,
                    'hotspot_duration' => $hotspot_duration,
                    'hotspot_latitude' => $hotspot_latitude,
                    'hotspot_longitude' => $hotspot_longitude,
                    'hotspot_distance' => $get_hotspot_travelling_distance,
                    'hotspot_location' => $hotspot_location,
                    'hotspot_priority' => $hotspot_priority,
                    'previous_hotspot_location' => $previous_hotspot_location
                ];
            endwhile;

            // Sort the hotspots based on the order specified in $order_by_location
            usort($hotspot_details, function($a, $b) use ($order_by_location) {
                // Get the positions of the IDs in the order array
                $posA = array_search($a['hotspot_ID'], $order_by_location);
                $posB = array_search($b['hotspot_ID'], $order_by_location);

                // Compare the positions
                return $posA - $posB; // Ascending order
            });

            $hotspot_eligibility_result = check_ITINEARY_HOTSPOT_CONFLICT($hotspot_details, $requested_hotspot_ID,$hotspot_siteseeing_travel_start_time, $staring_location_latitude, $staring_location_longtitude, $max_route_end_time, $dayOfWeekNumeric,$order_by_location);
        endif;
        
        /* print_r($hotspot_eligibility_result);
        exit; */

        $eligible_hotspots = $hotspot_eligibility_result['eligible_hotspots'];
        $conflicting_hotspots = $hotspot_eligibility_result['conflicting_hotspots'];
        
        if (!empty($conflicting_hotspots)):
            $response['success'] = false;
            $errors['hotspot_operating_hours_not_available'] = true;
            $errors['hotspot_operating_hours_not_available_dayOfWeekNumeric'] = $dayOfWeekNumeric;
            $errors['hotspot_operating_hours_not_available_hotspot_ID'] = $conflicting_hotspots;
            $errors['hotspot_operating_hours_not_available_itinerary_plan_ID'] = $itinerary_plan_ID;
            $errors['hotspot_operating_hours_not_available_itinerary_route_ID'] = $itinerary_route_ID;
            $errors['try_to_add_new_hotspot_ID'] = $requested_hotspot_ID;
        endif;

        // Step 2: Allocate Hotspots
        if (!empty($errors)) :
            $response['success'] = false;
            $response['errors'] = $errors;
        else:
            $response['success'] = true;

            foreach ($hotspot_details as $hotspot) :
                $hotspot_ID = $hotspot['hotspot_ID'];
                $hotspot_travel_time = $hotspot['hotspot_travel_time'];
                $hotspot_distance = $hotspot['hotspot_distance'];
                $hotspot_latitude = $hotspot['hotspot_latitude'];
                $hotspot_longitude = $hotspot['hotspot_longitude'];
                $hotspot_duration = $hotspot['hotspot_duration'];
                $hotspot_name = $hotspot['hotspot_name'];
                $hotspot_location = $hotspot['hotspot_location'];

                $hotspot_amout = 0;
                $hotspot_adult_entry_cost = 0;
                $hotspot_child_entry_cost = 0;
                $hotspot_infant_entry_cost = 0;
                $hotspot_foreign_adult_entry_cost = 0;
                $hotspot_foreign_child_entry_cost = 0;
                $hotspot_foreign_infant_entry_cost = 0;

                // Check if entry ticket is required
                if ($entry_ticket_required == 1) :
                    $total_adult = $total_adult;
                    $total_children = $total_children;
                    $total_infants = $total_infants;

                    $hotspot_adult_entry_cost = getHOTSPOT_CHARGES_DETAILS($hotspot_ID, 'hotspot_adult_entry_cost');
                    $hotspot_child_entry_cost = getHOTSPOT_CHARGES_DETAILS($hotspot_ID, 'hotspot_child_entry_cost');
                    $hotspot_infant_entry_cost = getHOTSPOT_CHARGES_DETAILS($hotspot_ID, 'hotspot_infant_entry_cost');
                    $hotspot_foreign_adult_entry_cost = getHOTSPOT_CHARGES_DETAILS($hotspot_ID, 'hotspot_foreign_adult_entry_cost');
                    $hotspot_foreign_child_entry_cost = getHOTSPOT_CHARGES_DETAILS($hotspot_ID, 'hotspot_foreign_child_entry_cost');
                    $hotspot_foreign_infant_entry_cost = getHOTSPOT_CHARGES_DETAILS($hotspot_ID, 'hotspot_foreign_infant_entry_cost');

                    if ($nationality != 101) :
                        $total_adult_cost = $total_adult * $hotspot_foreign_adult_entry_cost;
                        $total_child_cost = $total_children * $hotspot_foreign_child_entry_cost;
                        $total_infant_cost = $total_infants * $hotspot_foreign_infant_entry_cost;

                        $hotspot_amout = $total_adult_cost + $total_child_cost + $total_infant_cost;
                    else :
                        $total_adult_cost = $total_adult * $hotspot_adult_entry_cost;
                        $total_child_cost = $total_children * $hotspot_child_entry_cost;
                        $total_infant_cost = $total_infants * $hotspot_infant_entry_cost;

                        $hotspot_amout = $total_adult_cost + $total_child_cost + $total_infant_cost;
                    endif;
                endif;

                // Split the time string into its components
                list($hours, $minutes, $seconds) = explode(":", $hotspot_duration);

                // Convert hours and minutes to floats
                $hours = (float)$hours;
                $minutes = (float)$minutes;

                // Convert minutes to a fraction of an hour
                $decimalMinutes = $minutes / 60;

                // Calculate the total hours
                $totalHours = $hours + $decimalMinutes;

                // Format the total spending duration
                $total_spend_duration = $totalHours . " Hrs";

                // Determine the travel location type
                $travel_location_type = getTravelLocationType($previous_hotspot_location, $hotspot_location);

                $result = calculateDistanceAndDuration($get_staring_location_latitude, $get_staring_location_longtitude, $hotspot_latitude, $hotspot_longitude, $travel_location_type);

                $hotspot_travelling_distance = number_format($result['distance'], 2, '.', '');
                $hotspot_traveling_time = $result['duration'];

                // EXTRACT THE HOURS AND MINUTES FROM THE DURATION STRING
                preg_match('/(\d+) hour/', $hotspot_traveling_time, $hoursMatch);
                preg_match('/(\d+) mins/', $hotspot_traveling_time, $minutesMatch);

                // INITIALIZE HOURS AND MINUTES TO ZERO
                $hours = 0;
                $minutes = 0;

                $hours = isset($hoursMatch[1]) ? intval($hoursMatch[1]) : 0;
                $minutes = isset($minutesMatch[1]) ? intval($minutesMatch[1]) : 0;

                // CALCULATE EXTRA HOURS IF MINUTES EXCEED 59
                $extraHours = floor($minutes / 60);
                $hours += $extraHours;
                $minutes %= 60; // REMAINING MINUTES AFTER ADDING TO HOURS

                // FORMAT HOURS AND MINUTES WITH LEADING ZEROS
                $formattedHours = str_pad($hours, 2, '0', STR_PAD_LEFT);
                $formattedMinutes = str_pad($minutes, 2, '0', STR_PAD_LEFT);

                // FORMAT THE TIME AS H:i:s
                $duration_formatted = sprintf('%02d:%02d:00', $formattedHours, $formattedMinutes);

                //CALCAULATE THE DURATIONS IN SECONDS
                $totalSeconds = ($hours * 3600) + ($minutes * 60);

                //CONVERT {hotspot_siteseeing_traveling_start_time} TO SECONDS
                $startTimeInSeconds = strtotime($hotspot_siteseeing_traveling_start_time);

                //ADD THE DURATION TO THE START TIME
                $newTimeInSeconds = $startTimeInSeconds + $totalSeconds;

                //CONVERT THE NEW TIME TO {hotspot_siteseeing_travel_end_time} H:i:s FORMAT
                $hotspot_siteseeing_travel_end_time = date('H:i:s', $newTimeInSeconds);

                $get_hotspot_duration_seconds = strtotime("1970-01-01 $hotspot_duration UTC");
                $get_hotspot_ending_time_seconds = strtotime($hotspot_siteseeing_travel_end_time) + $get_hotspot_duration_seconds;

                //CONVERT THE NEW TIME TO {hotspot_siteseeing_end_time} H:i:s FORMAT
                $get_hotspot_ending_time = date('H:i:s', $get_hotspot_ending_time_seconds);

                $hotspot_siteseeing_end_time = $get_hotspot_ending_time;

                // Check if hotspot is already in the itinerary
                $check_hotspot_travel_already_added = sqlQUERY_LABEL("SELECT `route_hotspot_ID` FROM `dvi_itinerary_route_hotspot_details` WHERE `deleted` = '0' AND `status` = '1' AND `itinerary_plan_ID` = '$itinerary_plan_ID' AND `itinerary_route_ID` = '$itinerary_route_ID' AND `hotspot_ID` = '$hotspot_ID' AND `item_type`='3'");
                $check_hotspot_travel_already_added_num_rows = sqlNUMOFROW_LABEL($check_hotspot_travel_already_added);

                $check_hotspot_siteseeing_already_added = sqlQUERY_LABEL("SELECT `route_hotspot_ID` FROM `dvi_itinerary_route_hotspot_details` WHERE `deleted` = '0' AND `status` = '1' AND `itinerary_plan_ID` = '$itinerary_plan_ID' AND `itinerary_route_ID` = '$itinerary_route_ID' AND `hotspot_ID` = '$hotspot_ID' AND `item_type`='4'");
                $check_hotspot_siteseeing_already_added_num_rows = sqlNUMOFROW_LABEL($check_hotspot_siteseeing_already_added);

                if ($check_hotspot_travel_already_added_num_rows == 0 && $check_hotspot_siteseeing_already_added_num_rows == 0) :
                    // Insert or update logic
                    $hotspot_order++;
                    $route_hotspot_traveling_arrFields = array(
                        '`itinerary_plan_ID`', '`itinerary_route_ID`', '`item_type`', '`hotspot_order`', 
                        '`hotspot_ID`', '`hotspot_traveling_time`', '`hotspot_travelling_distance`', 
                        '`hotspot_start_time`', '`hotspot_end_time`', '`createdby`', '`status`'
                    );
                    $route_hotspot_traveling_arrValues = array(
                        "$itinerary_plan_ID", "$itinerary_route_ID", "3", "$hotspot_order", 
                        "$hotspot_ID", "$duration_formatted", "$hotspot_travelling_distance", 
                        "$hotspot_siteseeing_traveling_start_time", "$hotspot_siteseeing_travel_end_time", 
                        "$logged_user_id", "1"
                    );

                    // Insert the itinerary hotspot traveling data
                    if (sqlACTIONS("INSERT", "dvi_itinerary_route_hotspot_details", $route_hotspot_traveling_arrFields, $route_hotspot_traveling_arrValues, '')) :
                        // Update the start time for the next hotspot
                        $hotspot_siteseeing_start_time = $hotspot_siteseeing_travel_end_time;

                        // Check if hotspot is already in the itinerary
                        $check_hotspot_break_time_already_added = sqlQUERY_LABEL("SELECT `route_hotspot_ID` FROM `dvi_itinerary_route_hotspot_details` WHERE `deleted` = '0' AND `status` = '1' AND `itinerary_plan_ID` = '$itinerary_plan_ID' AND `itinerary_route_ID` = '$itinerary_route_ID' AND `hotspot_ID` = '$hotspot_ID' AND `item_type`='3' and `allow_break_hours` = '1'");
                        $check_hotspot_break_time_already_added_num_rows = sqlNUMOFROW_LABEL($check_hotspot_break_time_already_added);
                        while ($fetch_hotspot_breaktime_data = sqlFETCHARRAY_LABEL($check_hotspot_break_time_already_added)) :
                            $breaktime_route_hotspot_ID = $fetch_hotspot_breaktime_data['route_hotspot_ID'];
                        endwhile;

                        // Fetch the hotspot timings for the specific day, hotspot ID, and valid status
                        $select_hotspot_timing_list_data = sqlQUERY_LABEL("SELECT `hotspot_start_time`, `hotspot_end_time`, `hotspot_open_all_time` FROM `dvi_hotspot_timing` WHERE `hotspot_ID` = '$hotspot_ID' AND `hotspot_timing_day` = '$dayOfWeekNumeric' AND `status` = '1' AND `deleted` = '0' AND (
                        -- First condition: The current time falls within a valid time range
                        '$hotspot_siteseeing_start_time' BETWEEN `hotspot_start_time` AND `hotspot_end_time`
                        -- OR Second condition: The current time is before the start of a future time range
                        OR `hotspot_start_time` > '$hotspot_siteseeing_start_time') AND `hotspot_open_all_time` != '1' ORDER BY `hotspot_start_time` ASC LIMIT 1") or die("#1-UNABLE_TO_COLLECT_HOTSPOT_PLACE_LIST:" . sqlERROR_LABEL());

                        $total_hotspot_num_rows_count = sqlNUMOFROW_LABEL($select_hotspot_timing_list_data);

                        // If there are operating hours defined for the hotspot on the specified day
                        if ($total_hotspot_num_rows_count > 0) :
                            // Initialize variables
                            $operating_hours = array();

                            // Fetch operating hours data
                            while ($fetch_hotspot_timing_data = sqlFETCHARRAY_LABEL($select_hotspot_timing_list_data)) :
                                $hotspot_start_time = $fetch_hotspot_timing_data['hotspot_start_time'];
                                $hotspot_end_time = $fetch_hotspot_timing_data['hotspot_end_time'];
                            endwhile;

                            // Calculate the break duration between the current sightseeing start time and the hotspot start time
                            $total_break_duration = strtotime($hotspot_start_time) - strtotime($hotspot_siteseeing_start_time);

                            // If there's a break duration, format and insert the break record into the database
                            if ($total_break_duration > 0) :
                                // Convert the time difference into hours, minutes, and seconds
                                $break_hours = floor($total_break_duration / 3600);
                                $break_minutes = floor(($total_break_duration / 60) % 60);
                                $break_seconds = $total_break_duration % 60;

                                // Format the break duration as HH:MM:SS
                                $total_break_duration_formatted = sprintf("%02d:%02d:%02d", $break_hours, $break_minutes, $break_seconds);

                                // Set the start and end time for the break (you might need to adjust these)
                                $break_start_time = $hotspot_siteseeing_start_time;
                                $break_end_time = date("H:i:s", strtotime($hotspot_siteseeing_start_time) + $total_break_duration); // end time after the break

                                // Prepare the fields and values for the SQL insert query
                                $break_arrFields = array(
                                    '`itinerary_plan_ID`', '`itinerary_route_ID`', '`item_type`', '`hotspot_order`', '`hotspot_ID`', '`hotspot_traveling_time`', '`hotspot_start_time`', '`hotspot_end_time`', '`allow_break_hours`', '`createdby`', '`status`'
                                );
                                $break_arrValues = array(
                                    "$itinerary_plan_ID", "$itinerary_route_ID", "3", "$hotspot_order", "$hotspot_ID", "$total_break_duration_formatted", "$break_start_time", "$break_end_time", "1","$logged_user_id", "1"
                                );

                                if($check_hotspot_break_time_already_added_num_rows == 0):
                                    // Insert the break record into the database
                                    $insert_break_time = sqlACTIONS("INSERT", "dvi_itinerary_route_hotspot_details", $break_arrFields, $break_arrValues, '');
                                else:
                                    $break_sqlWhere = " `route_hotspot_ID` = '$breaktime_route_hotspot_ID' ";
                                    $update_break_time = sqlACTIONS("UPDATE", "dvi_itinerary_route_hotspot_details", $break_arrFields, $break_arrValues, $break_sqlWhere);
                                endif;
                                // Update the sightseeing start time to the hotspot start time after the break
                                $hotspot_siteseeing_start_time = $break_end_time;
                            else:
                                if($check_hotspot_break_time_already_added_num_rows > 0):
                                    $break_sqlWhere = " `route_hotspot_ID` = '$breaktime_route_hotspot_ID' ";
                                    $delete_break_time = sqlACTIONS("DELETE", "dvi_itinerary_route_hotspot_details", '', '', $break_sqlWhere);
                                endif;
                            endif;
                        endif;
                        
                        // Convert the duration to seconds and add it to the start time
                        $hotspot_duration_seconds = strtotime("1970-01-01 $hotspot_duration UTC");
                        $hotspot_siteseeing_new_end_time = strtotime($hotspot_siteseeing_start_time) + $hotspot_duration_seconds;

                        // Convert the new time to H:i:s format
                        $hotspot_siteseeing_end_time = date('H:i:s', $hotspot_siteseeing_new_end_time);

                        $route_hotspot_arrFields = array(
                            '`itinerary_plan_ID`', '`itinerary_route_ID`', '`item_type`', '`hotspot_order`', 
                            '`hotspot_ID`', '`hotspot_adult_entry_cost`', '`hotspot_child_entry_cost`', 
                            '`hotspot_infant_entry_cost`', '`hotspot_foreign_adult_entry_cost`', 
                            '`hotspot_foreign_child_entry_cost`', '`hotspot_foreign_infant_entry_cost`', 
                            '`hotspot_amout`', '`hotspot_traveling_time`', '`hotspot_start_time`', 
                            '`hotspot_end_time`', '`createdby`', '`status`'
                        );
                        $route_hotspot_arrValues = array(
                            "$itinerary_plan_ID", "$itinerary_route_ID", "4", "$hotspot_order", 
                            "$hotspot_ID", "$hotspot_adult_entry_cost", "$hotspot_child_entry_cost", 
                            "$hotspot_infant_entry_cost", "$hotspot_foreign_adult_entry_cost", 
                            "$hotspot_foreign_child_entry_cost", "$hotspot_foreign_infant_entry_cost", 
                            "$hotspot_amout", "$hotspot_duration", "$hotspot_siteseeing_start_time", 
                            "$hotspot_siteseeing_end_time", "$logged_user_id", "1"
                        );

                        // Insert the itinerary hotspot sightseeing place data
                        if (sqlACTIONS("INSERT", "dvi_itinerary_route_hotspot_details", $route_hotspot_arrFields, $route_hotspot_arrValues, '')) :
                            $hotspot_siteseeing_traveling_start_time = $hotspot_siteseeing_end_time;
                            $get_staring_location_latitude = $hotspot_latitude;
                            $get_staring_location_longtitude = $hotspot_longitude;
                            $previous_hotspot_location = $hotspot_location;

                            # INSERT THE TRAVELLER WISE HOTSPOT ENTRY CHARGES
                            if ($entry_ticket_required == 1) :
                                $route_hotspot_ID = sqlINSERTID_LABEL();
                                
                                # COLLECT ALL THE TRAVELLER DETAILS
                                $traveller_data = [
                                    'adult' => [
                                        'count' => get_ITINEARY_PLAN_DETAILS($itinerary_plan_ID, 'total_adult'),
                                        'type' => 1,
                                        'cost_field' => 'hotspot_adult_entry_cost',
                                        'foreign_cost_field' => 'hotspot_foreign_adult_entry_cost'
                                    ],
                                    'child' => [
                                        'count' => get_ITINEARY_PLAN_DETAILS($itinerary_plan_ID, 'total_children'),
                                        'type' => 2,
                                        'cost_field' => 'hotspot_child_entry_cost',
                                        'foreign_cost_field' => 'hotspot_foreign_child_entry_cost'
                                    ],
                                    'infant' => [
                                        'count' => get_ITINEARY_PLAN_DETAILS($itinerary_plan_ID, 'total_infants'),
