                $staring_location_name = getSTOREDLOCATIONDETAILS($start_location_id, 'DESTINATION_LOCATION');
                $hotspot_order = 2;
            else:
                $staring_location_latitude = getSTOREDLOCATION_VIAROUTE_DETAILS($start_location_id,$via_route_name[0],'VIAROUTE_LATTITUDE');
                $staring_location_longtitude = getSTOREDLOCATION_VIAROUTE_DETAILS($start_location_id,$via_route_name[0],'VIAROUTE_LONGITUDE');
                $staring_location_name = $via_route_name[0];
                $hotspot_order = 2;
            endif;
        endif;

        /* if ($get_starting_location_item_type == 1) :
            $staring_location_latitude = getSTOREDLOCATIONDETAILS($start_location_id, 'source_location_lattitude');
            $staring_location_longtitude = getSTOREDLOCATIONDETAILS($start_location_id, 'source_location_longitude');
            $staring_location_name = getSTOREDLOCATIONDETAILS($start_location_id, 'SOURCE_LOCATION');
            $hotspot_order = 1;
        elseif($get_starting_location_item_type == 2) :
            $staring_location_latitude = getSTOREDLOCATIONDETAILS($start_location_id, 'destination_location_lattitude');
            $staring_location_longtitude = getSTOREDLOCATIONDETAILS($start_location_id, 'destination_location_longitude');
            $staring_location_name = getSTOREDLOCATIONDETAILS($start_location_id, 'DESTINATION_LOCATION');
            $hotspot_order = 2;
        endif; */

        // Convert the date string to a Unix timestamp using strtotime
        $timestamp = strtotime($itinerary_route_date);

        if ($timestamp !== false) :
            // Get the numeric representation of the day of the week (0 for Sunday, 1 for Monday, etc.)
            $dayOfWeekNumeric = (int)date('N', $timestamp) - 1;
        endif;

         $selected_hotspot_activity_data = sqlQUERY_LABEL("SELECT `activity_order`, `activity_end_time` FROM `dvi_itinerary_route_activity_details` WHERE `status`='1' AND `deleted`='0' AND `itinerary_plan_ID` = '$itinerary_plan_ID' AND `itinerary_route_ID` = '$itinerary_route_ID' AND `route_hotspot_ID` = '$route_hotspot_ID' AND `hotspot_ID` = '$hotspot_ID'") or die("#1-getITINEARY_ROUTE_HOTSPOT_DETAILS: " . sqlERROR_LABEL());
        if (sqlNUMOFROW_LABEL($selected_hotspot_activity_data) > 0) :
            while ($fetch_hotspot_time_data = sqlFETCHARRAY_LABEL($selected_hotspot_activity_data)) :
                $activity_order = $fetch_hotspot_time_data['activity_order'];
                $hotspot_activity_start_time = $fetch_hotspot_time_data['activity_end_time'];
            endwhile;
        else :
            $selected_hotspot_query_data = sqlQUERY_LABEL("SELECT `hotspot_order`, `hotspot_end_time` FROM `dvi_itinerary_route_hotspot_details` WHERE `status`='1' AND `deleted`='0' AND `itinerary_plan_ID` = '$itinerary_plan_ID' AND `itinerary_route_ID` = '$itinerary_route_ID' AND `item_type` = '4' AND `route_hotspot_ID` = '$route_hotspot_ID'") or die("#1-getITINEARY_ROUTE_HOTSPOT_DETAILS: " . sqlERROR_LABEL());
            if (sqlNUMOFROW_LABEL($selected_hotspot_query_data) > 0) :
                while ($fetch_hotspot_time_data = sqlFETCHARRAY_LABEL($selected_hotspot_query_data)) :
                    $hotspot_order = $fetch_hotspot_time_data['hotspot_order'];
                    $hotspot_activity_start_time = $fetch_hotspot_time_data['hotspot_end_time'];
                endwhile;
            endif;
            $activity_order = 1;
        endif;

        $select_hotspot_activity_details = sqlQUERY_LABEL("SELECT `activity_duration` FROM `dvi_activity` WHERE `deleted` = '0' AND `status` = '1' AND `hotspot_id` = '$hotspot_ID' AND `activity_id` = '$activity_ID'") or die("#1-UNABLE_TO_COLLECT_HOTSPOT_ACTIVITY_LIST:" . sqlERROR_LABEL());
        while ($fetch_hotspot_activity_data = sqlFETCHARRAY_LABEL($select_hotspot_activity_details)) :
            $activity_duration = $fetch_hotspot_activity_data['activity_duration'];
        endwhile;

        /* if ($entry_ticket_required == 1) : */

        $get_activity_charges_for_foreign_adult = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_foreign_adult');
        $get_activity_charges_for_foreign_children = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_foreign_children');
        $get_activity_charges_for_foreign_infant = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_foreign_infant');
        $get_activity_charges_for_adult = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_adult');
        $get_activity_charges_for_children = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_children');
        $get_activity_charges_for_infant = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_infant');

        if ($nationality != 101) :
            $get_activity_charges_for_adult = $get_activity_charges_for_foreign_adult;
            $get_activity_charges_for_children = $get_activity_charges_for_foreign_children;
            $get_activity_charges_for_infant = $get_activity_charges_for_foreign_infant;
        else :
            $get_activity_charges_for_adult = $get_activity_charges_for_adult;
            $get_activity_charges_for_children = $get_activity_charges_for_children;
            $get_activity_charges_for_infant = $get_activity_charges_for_infant;
        endif;

        $total_adult_activity_changes = $total_adult * $get_activity_charges_for_adult;
        $total_children_activity_changes = $total_children * $get_activity_charges_for_children;
        $total_infant_activity_changes = $total_infants * $get_activity_charges_for_infant;
        $total_activity_changes = $total_adult_activity_changes + $total_children_activity_changes + $total_infant_activity_changes;
        /* endif; */

        // Convert start time to seconds
        list($start_hour, $start_minute, $start_second) = explode(":", $hotspot_activity_start_time);
        $start_hour = (int)$start_hour;
        $start_minute = (int)$start_minute;
        $start_second = (int)$start_second;
        $start_time_seconds = $start_hour * 3600 + $start_minute * 60 + $start_second;

        // Convert duration to seconds
        list($duration_hour, $duration_minute, $duration_second) = explode(":", $activity_duration);
        $duration_hour = (int)$duration_hour;
        $duration_minute = (int)$duration_minute;
        $duration_second = (int)$duration_second;
        $duration_seconds = $duration_hour * 3600 + $duration_minute * 60 + $duration_second;

        // Add duration to start time
        $total_time_seconds = $start_time_seconds + $duration_seconds;

        // Convert total time back to H:i:s format
        $hotspot_activity_end_time = gmdate("H:i:s", $total_time_seconds);

        $after_activity_added_hotspot_start_time = $hotspot_activity_end_time;

        // Array to store conflicting hotspots
        $conflicting_hotspots = array();

        // Loop through each hotspot to check operating hours
        $all_operating_hours_available = true;
        $selected_after_activity_added_hotspot_query_data = sqlQUERY_LABEL("SELECT `route_hotspot_ID`, `item_type`, `hotspot_ID`, `hotspot_traveling_time` FROM `dvi_itinerary_route_hotspot_details` WHERE `status`='1' AND `deleted`='0' AND `itinerary_plan_ID` = '$itinerary_plan_ID' AND `itinerary_route_ID` = '$itinerary_route_ID' AND `route_hotspot_ID` > '$route_hotspot_ID' AND `item_type` IN ('3','4') ORDER BY `hotspot_order`,`item_type` ASC") or die("#1-getITINEARY_ROUTE_HOTSPOT_DETAILS: " . sqlERROR_LABEL());
        if (sqlNUMOFROW_LABEL($selected_after_activity_added_hotspot_query_data) > 0) :
            while ($fetch_hotspot_time_data = sqlFETCHARRAY_LABEL($selected_after_activity_added_hotspot_query_data)) :
                $after_activity_added_route_hotspot_ID = $fetch_hotspot_time_data['route_hotspot_ID'];
                $after_activity_added_item_type = $fetch_hotspot_time_data['item_type'];
                $after_activity_added_hotspot_ID = $fetch_hotspot_time_data['hotspot_ID'];
                $after_activity_added_hotspot_traveling_time = $fetch_hotspot_time_data['hotspot_traveling_time'];

                // Convert start time to seconds
                list($start_hour, $start_minute, $start_second) = explode(":", $after_activity_added_hotspot_start_time);
                $after_activity_added_start_time_seconds = intval($start_hour) * 3600 + intval($start_minute) * 60 + intval($start_second);

                // Convert traveling time to seconds
                list($travel_hour, $travel_minute, $travel_second) = explode(":", $after_activity_added_hotspot_traveling_time);
                $after_activity_added_travel_seconds = intval($travel_hour) * 3600 + intval($travel_minute) * 60 + intval($travel_second);

                // Add traveling time to start time
                $after_activity_added_total_time_seconds = $after_activity_added_start_time_seconds + $after_activity_added_travel_seconds;

                // Convert total time back to H:i:s format
                $after_activity_added_hotspot_end_time = gmdate("H:i:s", $after_activity_added_total_time_seconds);

                // Check operating hours for the current hotspot
                $operating_hours_available = checkHOTSPOTOPERATINGHOURS($after_activity_added_hotspot_ID, $dayOfWeekNumeric, $after_activity_added_hotspot_start_time, $after_activity_added_hotspot_end_time,$itinerary_plan_ID, $itinerary_route_ID, $hotspot_order, $logged_user_id);

                /* if (!$operating_hours_available) :
                    // Operating hours not available for the current hotspot
                    $all_operating_hours_available = false;
                    $get_hotspot_name = getHOTSPOTDETAILS($after_activity_added_hotspot_ID, 'label');
                    break; // Exit loop if any hotspot's operating hours are not available
                endif; */

                if (!$operating_hours_available) :
                    // Operating hours not available for the current hotspot
                    $get_hotspot_name = getHOTSPOTDETAILS($after_activity_added_hotspot_ID, 'label');
                    
                    // Check if the hotspot is already in the array to prevent duplication
                    if (!in_array($after_activity_added_hotspot_ID, $conflicting_hotspots)):
                        $conflicting_hotspots[] = $after_activity_added_hotspot_ID;
                    endif;
                    // Continue to the next hotspot
                    continue;
                endif;
                $after_activity_added_hotspot_start_time = $after_activity_added_hotspot_end_time;
            endwhile;
        else :
            $after_activity_added_hotspot_end_time = $hotspot_activity_end_time;
        endif;

        $check_hotspot_activity_already_added = sqlQUERY_LABEL("SELECT `route_activity_ID` FROM `dvi_itinerary_route_activity_details` WHERE `status`='1' AND `deleted`='0' AND `itinerary_plan_ID` = '$itinerary_plan_ID' AND `itinerary_route_ID` = '$itinerary_route_ID' AND `activity_ID` = '$activity_ID'") or die("#1-getITINEARY_ROUTE_HOTSPOT_DETAILS: " . sqlERROR_LABEL());
        $total_hotspot_activity_num_rows_count = sqlNUMOFROW_LABEL($check_hotspot_activity_already_added);

        if ($total_hotspot_activity_num_rows_count > 0) :
            $errors['activity_already_exist'] = "This activity already added in this route";
        endif;

        if (!empty($errors)) :
            //error call
            $response['success'] = false;
            $response['errors'] = $errors;
        else :
            //success call		
            $response['success'] = true;
            // Check if all operating hours are available for hotspots
            if ($all_operating_hours_available) :
                // All hotspots have operating hours available
                /* if ($after_activity_added_hotspot_end_time <= $route_end_time) : */
                $activity_order++;
                $route_hotspot_activity_arrFields = array('`itinerary_plan_ID`', '`itinerary_route_ID`', '`route_hotspot_ID`', '`hotspot_ID`', '`activity_ID`', '`activity_order`', '`activity_charges_for_foreign_adult`', '`activity_charges_for_foreign_children`', '`activity_charges_for_foreign_infant`', '`activity_charges_for_adult`', '`activity_charges_for_children`', '`activity_charges_for_infant`', '`activity_amout`', '`activity_traveling_time`', '`activity_start_time`', '`activity_end_time`', '`createdby`', '`status`');
                $route_hotspot_activity_arrValues = array("$itinerary_plan_ID", "$itinerary_route_ID", "$route_hotspot_ID", "$hotspot_ID", "$activity_ID", "$activity_order", "$get_activity_charges_for_foreign_adult", "$get_activity_charges_for_foreign_children", "$get_activity_charges_for_foreign_infant", "$get_activity_charges_for_adult", "$get_activity_charges_for_children", "$get_activity_charges_for_infant", "$total_activity_changes", "$activity_duration", "$hotspot_activity_start_time", "$hotspot_activity_end_time", "$logged_user_id", "1");

                //INSERT THE ITINEARY HOTSPOT ACTIVITY DATA
                if (sqlACTIONS("INSERT", "dvi_itinerary_route_activity_details", $route_hotspot_activity_arrFields, $route_hotspot_activity_arrValues, '')) :

					$route_activity_ID = sqlINSERTID_LABEL();

					# COLLECT ALL THE TRAVELLER DETAILS
					$traveller_data = [
						'adult' => [
							'count' => get_ITINEARY_PLAN_DETAILS($itinerary_plan_ID, 'total_adult'),
							'type' => 1,
							'cost_field' => 'get_activity_charges_for_adult',
							'foreign_cost_field' => 'get_activity_charges_for_foreign_adult'
						],
						'child' => [
							'count' => get_ITINEARY_PLAN_DETAILS($itinerary_plan_ID, 'total_children'),
							'type' => 2,
							'cost_field' => 'get_activity_charges_for_children',
							'foreign_cost_field' => 'get_activity_charges_for_foreign_children'
						],
						'infant' => [
							'count' => get_ITINEARY_PLAN_DETAILS($itinerary_plan_ID, 'total_infants'),
							'type' => 3,
							'cost_field' => 'get_activity_charges_for_infant',
							'foreign_cost_field' => 'get_activity_charges_for_foreign_infant'
						],
					];

					$get_nationality = get_ITINEARY_PLAN_DETAILS($itinerary_plan_ID, 'nationality');

					# INSERT THE TRAVELLER WISE HOTSPOT ENTRY CHARGES
					foreach ($traveller_data as $key => $data) :
						if ($data['count'] > 0) :
							for ($i = 1; $i <= $data['count']; $i++) :
								$traveller_name = ucfirst($key) . " $i";
								$traveller_type = $data['type'];
								$entry_ticket_cost = ($get_nationality != '101') ? get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($route_activity_ID,$itinerary_route_date, $data['foreign_cost_field']) : get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($route_activity_ID,$itinerary_route_date, $data['cost_field']);

								# ENTRY COST SHOULD BE GRATER THEN ZERO ONLY
								if ($entry_ticket_cost > 0) :

									$hotspot_cost_details_fields = ['`itinerary_plan_id`', '`itinerary_route_id`', '`route_activity_id`','`hotspot_ID`', '`activity_ID`', '`traveller_type`', '`traveller_name`', '`entry_ticket_cost`', '`createdby`', '`status`'];
									$hotspot_cost_details_values = ["$itinerary_plan_ID", "$itinerary_route_ID", "$hotspot_ID","$route_activity_ID", "$hotspot_ID", "$activity_ID", "$traveller_type", "$traveller_name", "$entry_ticket_cost", "$logged_user_id", "1"];

									$entry_cost_sqlwhere = " `route_activity_id` = '$route_activity_ID' AND `itinerary_plan_id` = '$itinerary_plan_ID' AND `itinerary_route_id` = '$itinerary_route_ID' AND `hotspot_ID` = '$hotspot_ID' AND `activity_ID` = '$activity_ID' AND `traveller_name` = '$traveller_name' ";

									if (sqlNUMOFROW_LABEL(sqlQUERY_LABEL("SELECT `hotspot_cost_detail_id` FROM `dvi_itinerary_route_activity_entry_cost_details` WHERE $entry_cost_sqlwhere")) > 0) :
										sqlACTIONS("UPDATE", "dvi_itinerary_route_activity_entry_cost_details", $hotspot_cost_details_fields, $hotspot_cost_details_values, $entry_cost_sqlwhere);
									else :
										sqlACTIONS("INSERT", "dvi_itinerary_route_activity_entry_cost_details", $hotspot_cost_details_fields, $hotspot_cost_details_values, '');
									endif;
								endif;
							endfor;
						endif;
					endforeach;
                endif;

                $after_activity_added_hotspot_start_time = $hotspot_activity_end_time;
                $selected_after_activity_added_hotspot_query_data = sqlQUERY_LABEL("SELECT HOTSPOT.`hotspot_latitude`, HOTSPOT.`hotspot_longitude`, HOTSPOT.`hotspot_location`, ROUTE_HOTSPOT.`route_hotspot_ID`, ROUTE_HOTSPOT.`item_type`, ROUTE_HOTSPOT.`hotspot_ID`, ROUTE_HOTSPOT.`hotspot_traveling_time` FROM `dvi_itinerary_route_hotspot_details` ROUTE_HOTSPOT LEFT JOIN `dvi_hotspot_place` HOTSPOT ON HOTSPOT.`hotspot_ID` = ROUTE_HOTSPOT.`hotspot_ID` WHERE ROUTE_HOTSPOT.`status`='1' AND ROUTE_HOTSPOT.`deleted`='0' AND ROUTE_HOTSPOT.`itinerary_plan_ID` = '$itinerary_plan_ID' AND ROUTE_HOTSPOT.`itinerary_route_ID` = '$itinerary_route_ID' AND ROUTE_HOTSPOT.`route_hotspot_ID` > '$route_hotspot_ID' AND ROUTE_HOTSPOT.`item_type` IN ('3','4') ORDER BY ROUTE_HOTSPOT.`hotspot_order`,ROUTE_HOTSPOT.`item_type` ASC") or die("#1-getITINEARY_ROUTE_HOTSPOT_DETAILS: " . sqlERROR_LABEL());
                if (sqlNUMOFROW_LABEL($selected_after_activity_added_hotspot_query_data) > 0) :
                    while ($fetch_hotspot_time_data = sqlFETCHARRAY_LABEL($selected_after_activity_added_hotspot_query_data)) :
                        $after_activity_added_route_hotspot_ID = $fetch_hotspot_time_data['route_hotspot_ID'];
                        $after_activity_added_item_type = $fetch_hotspot_time_data['item_type'];
                        $after_activity_added_hotspot_ID = $fetch_hotspot_time_data['hotspot_ID'];
                        $after_activity_added_hotspot_traveling_time = $fetch_hotspot_time_data['hotspot_traveling_time'];
                        $after_activity_added_hotspot_latitude = $fetch_hotspot_time_data['hotspot_latitude'];
                        $after_activity_added_hotspot_longitude = $fetch_hotspot_time_data['hotspot_longitude'];
                        $after_activity_added_hotspot_location = $fetch_hotspot_time_data['hotspot_location'];

                        // Convert start time to seconds
                        list($start_hour, $start_minute, $start_second) = explode(":", $after_activity_added_hotspot_start_time);
                        $after_activity_added_start_time_seconds = intval($start_hour) * 3600 + intval($start_minute) * 60 + intval($start_second);

                        // Convert traveling time to seconds
                        list($travel_hour, $travel_minute, $travel_second) = explode(":", $after_activity_added_hotspot_traveling_time);
                        $after_activity_added_travel_seconds = intval($travel_hour) * 3600 + intval($travel_minute) * 60 + intval($travel_second);

                        // Add traveling time to start time
                        $after_activity_added_total_time_seconds = $after_activity_added_start_time_seconds + $after_activity_added_travel_seconds;

                        // Convert total time back to H:i:s format
                        $after_activity_added_hotspot_end_time = gmdate("H:i:s", $after_activity_added_total_time_seconds);

                        $route_hotspot_arrFields = array('`hotspot_start_time`', '`hotspot_end_time`');
                        $route_hotspot_arrValues = array("$after_activity_added_hotspot_start_time", "$after_activity_added_hotspot_end_time");
                        $route_hotspot_sqlwhere = " `itinerary_plan_ID` = '$itinerary_plan_ID' AND `itinerary_route_ID` = '$itinerary_route_ID' AND `route_hotspot_ID` = '$after_activity_added_route_hotspot_ID' AND `item_type` = '$after_activity_added_item_type' ";
                        //UPDATE THE ITINEARY HOTSPOT TIMING DATA
                        if (sqlACTIONS("UPDATE", "dvi_itinerary_route_hotspot_details", $route_hotspot_arrFields, $route_hotspot_arrValues, $route_hotspot_sqlwhere)) :

                            if ($after_activity_added_item_type == 4) :

                                $check_hotspot_activity_available = sqlQUERY_LABEL("SELECT `route_activity_ID`, `activity_ID` FROM `dvi_itinerary_route_activity_details` WHERE `status`='1' AND `deleted`='0' AND `itinerary_plan_ID` = '$itinerary_plan_ID' AND `itinerary_route_ID` = '$itinerary_route_ID' AND `hotspot_ID` = '$after_activity_added_hotspot_ID' AND `route_hotspot_ID` = '$after_activity_added_route_hotspot_ID' ORDER BY `route_activity_ID`") or die("#1-getITINEARY_ROUTE_HOTSPOT_DETAILS: " . sqlERROR_LABEL());
                                $total_hotspot_activity_num_rows_count = sqlNUMOFROW_LABEL($check_hotspot_activity_available);

                                if ($total_hotspot_activity_num_rows_count > 0) :
                                    $activity_order = 1;
                                    while ($fetch_itinerary_hotspot_activity_data = sqlFETCHARRAY_LABEL($check_hotspot_activity_available)) :
                                        $activity_order++;
                                        $route_activity_ID = $fetch_itinerary_hotspot_activity_data['route_activity_ID'];
                                        $activity_ID = $fetch_itinerary_hotspot_activity_data['activity_ID'];

                                        $select_hotspot_activity_details = sqlQUERY_LABEL("SELECT `activity_duration` FROM `dvi_activity` WHERE `deleted` = '0' AND `status` = '1' AND `hotspot_id` = '$after_activity_added_hotspot_ID' AND `activity_id` = '$activity_ID'") or die("#1-UNABLE_TO_COLLECT_HOTSPOT_ACTIVITY_LIST:" . sqlERROR_LABEL());
                                        while ($fetch_hotspot_activity_data = sqlFETCHARRAY_LABEL($select_hotspot_activity_details)) :
                                            $activity_duration = $fetch_hotspot_activity_data['activity_duration'];
                                        endwhile;

                                        /* if ($entry_ticket_required == 1) : */
                                        $get_activity_charges_for_foreign_adult = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_foreign_adult');
                                        $get_activity_charges_for_foreign_children = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_foreign_children');
                                        $get_activity_charges_for_foreign_infant = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_foreign_infant');
                                        $get_activity_charges_for_adult = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_adult');
                                        $get_activity_charges_for_children = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_children');
                                        $get_activity_charges_for_infant = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_infant');

                                        if ($nationality != 101) :
                                            $get_activity_charges_for_adult = $get_activity_charges_for_foreign_adult;
                                            $get_activity_charges_for_children = $get_activity_charges_for_foreign_children;
                                            $get_activity_charges_for_infant = $get_activity_charges_for_foreign_infant;
                                        else :
                                            $get_activity_charges_for_adult = $get_activity_charges_for_adult;
                                            $get_activity_charges_for_children = $get_activity_charges_for_children;
                                            $get_activity_charges_for_infant = $get_activity_charges_for_infant;
                                        endif;

                                        $total_adult_activity_changes = $total_adult * $get_activity_charges_for_adult;
                                        $total_children_activity_changes = $total_children * $get_activity_charges_for_children;
                                        $total_infant_activity_changes = $total_infants * $get_activity_charges_for_infant;
