                        list($start_hour, $start_minute, $start_second) = explode(":", $after_activity_added_hotspot_start_time);
                        $after_activity_added_start_time_seconds = intval($start_hour) * 3600 + intval($start_minute) * 60 + intval($start_second);

                        // Convert traveling time to seconds
                        list($travel_hour, $travel_minute, $travel_second) = explode(":", $after_activity_added_hotspot_traveling_time);
                        $after_activity_added_travel_seconds = intval($travel_hour) * 3600 + intval($travel_minute) * 60 + intval($travel_second);

                        // Add traveling time to start time
                        $after_activity_added_total_time_seconds = $after_activity_added_start_time_seconds + $after_activity_added_travel_seconds;

                        // Convert total time back to H:i:s format
                        $after_activity_added_hotspot_end_time = gmdate("H:i:s", $after_activity_added_total_time_seconds);

                        $route_hotspot_arrFields = array('`hotspot_start_time`', '`hotspot_end_time`');
                        $route_hotspot_arrValues = array("$after_activity_added_hotspot_start_time", "$after_activity_added_hotspot_end_time");
                        $route_hotspot_sqlwhere = " `itinerary_plan_ID` = '$itinerary_plan_ID' AND `itinerary_route_ID` = '$itinerary_route_ID' AND `route_hotspot_ID` = '$after_activity_added_route_hotspot_ID' AND `item_type` = '$after_activity_added_item_type' ";
                        //UPDATE THE ITINEARY HOTSPOT TIMING DATA
                        if (sqlACTIONS("UPDATE", "dvi_itinerary_route_hotspot_details", $route_hotspot_arrFields, $route_hotspot_arrValues, $route_hotspot_sqlwhere)) :

                            if ($after_activity_added_item_type == 4) :

                                $check_hotspot_activity_available = sqlQUERY_LABEL("SELECT `route_activity_ID`, `activity_ID` FROM `dvi_itinerary_route_activity_details` WHERE `status`='1' AND `deleted`='0' AND `itinerary_plan_ID` = '$itinerary_plan_ID' AND `itinerary_route_ID` = '$itinerary_route_ID' AND `hotspot_ID` = '$after_activity_added_hotspot_ID' AND `route_hotspot_ID` = '$after_activity_added_route_hotspot_ID' ORDER BY `route_activity_ID`") or die("#1-getITINEARY_ROUTE_HOTSPOT_DETAILS: " . sqlERROR_LABEL());
                                $total_hotspot_activity_num_rows_count = sqlNUMOFROW_LABEL($check_hotspot_activity_available);

                                if ($total_hotspot_activity_num_rows_count > 0) :
                                    $activity_order = 1;
                                    while ($fetch_itinerary_hotspot_activity_data = sqlFETCHARRAY_LABEL($check_hotspot_activity_available)) :
                                        $activity_order++;
                                        $route_activity_ID = $fetch_itinerary_hotspot_activity_data['route_activity_ID'];
                                        $activity_ID = $fetch_itinerary_hotspot_activity_data['activity_ID'];

                                        $select_hotspot_activity_details = sqlQUERY_LABEL("SELECT `activity_duration` FROM `dvi_activity` WHERE `deleted` = '0' AND `status` = '1' AND `hotspot_id` = '$after_activity_added_hotspot_ID' AND `activity_id` = '$activity_ID'") or die("#1-UNABLE_TO_COLLECT_HOTSPOT_ACTIVITY_LIST:" . sqlERROR_LABEL());
                                        while ($fetch_hotspot_activity_data = sqlFETCHARRAY_LABEL($select_hotspot_activity_details)) :
                                            $activity_duration = $fetch_hotspot_activity_data['activity_duration'];
                                        endwhile;

                                        /* if ($entry_ticket_required == 1) : */
                                        $get_activity_charges_for_foreign_adult = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_foreign_adult');
                                        $get_activity_charges_for_foreign_children = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_foreign_children');
                                        $get_activity_charges_for_foreign_infant = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_foreign_infant');
                                        $get_activity_charges_for_adult = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_adult');
                                        $get_activity_charges_for_children = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_children');
                                        $get_activity_charges_for_infant = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_infant');

                                        if ($nationality != 101) :
                                            $get_activity_charges_for_adult = $get_activity_charges_for_foreign_adult;
                                            $get_activity_charges_for_children = $get_activity_charges_for_foreign_children;
                                            $get_activity_charges_for_infant = $get_activity_charges_for_foreign_infant;
                                        else :
                                            $get_activity_charges_for_adult = $get_activity_charges_for_adult;
                                            $get_activity_charges_for_children = $get_activity_charges_for_children;
                                            $get_activity_charges_for_infant = $get_activity_charges_for_infant;
                                        endif;

                                        $total_adult_activity_changes = $total_adult * $get_activity_charges_for_adult;
                                        $total_children_activity_changes = $total_children * $get_activity_charges_for_children;
                                        $total_infant_activity_changes = $total_infants * $get_activity_charges_for_infant;
                                        $total_activity_changes = $total_adult_activity_changes + $total_children_activity_changes + $total_infant_activity_changes;
                                        /* endif; */

                                        $hotspot_activity_start_time = $after_activity_added_hotspot_end_time;

                                        // Convert start time to seconds
                                        list($start_hour, $start_minute, $start_second) = explode(":", $hotspot_activity_start_time);
                                        $start_time_seconds = $start_hour * 3600 + $start_minute * 60 + $start_second;

                                        // Convert duration to seconds
                                        list($duration_hour, $duration_minute, $duration_second) = explode(":", $activity_duration);
                                        $duration_seconds = $duration_hour * 3600 + $duration_minute * 60 + $duration_second;

                                        // Add duration to start time
                                        $total_time_seconds = $start_time_seconds + $duration_seconds;

                                        // Convert total time back to H:i:s format
                                        $hotspot_activity_end_time = gmdate("H:i:s", $total_time_seconds);

                                        $route_hotspot_activity_arrFields = array('`activity_order`', '`activity_charges_for_foreign_adult`', '`activity_charges_for_foreign_children`', '`activity_charges_for_foreign_infant`', '`activity_charges_for_adult`', '`activity_charges_for_children`', '`activity_charges_for_infant`', '`activity_amout`', '`activity_traveling_time`', '`activity_start_time`', '`activity_end_time`');
                                        $route_hotspot_activity_arrValues = array("$activity_order", "$get_activity_charges_for_foreign_adult", "$get_activity_charges_for_foreign_children", "$get_activity_charges_for_foreign_infant", "$get_activity_charges_for_adult", "$get_activity_charges_for_children", "$get_activity_charges_for_infant", "$total_activity_changes", "$activity_duration", "$hotspot_activity_start_time", "$hotspot_activity_end_time");
                                        $route_hotspot_activity_sqlwhere = " `itinerary_plan_ID` = '$itinerary_plan_ID' AND `itinerary_route_ID` = '$itinerary_route_ID' AND `route_hotspot_ID` = '$after_activity_added_route_hotspot_ID' AND `hotspot_ID` = '$after_activity_added_hotspot_ID' AND `activity_ID` = '$activity_ID' ";

                                        //INSERT THE ITINEARY HOTSPOT ACTIVITY DATA
                                        if (sqlACTIONS("UPDATE", "dvi_itinerary_route_activity_details", $route_hotspot_activity_arrFields, $route_hotspot_activity_arrValues, $route_hotspot_activity_sqlwhere)) :

                                            $hotspot_activity_start_time = $hotspot_activity_end_time;
                                        endif;

                                    endwhile;

                                    //UPDATE STARTING LOCATION AND TIME DETAILS FOR NEXT ITERATION
                                    $after_activity_added_hotspot_start_time = $hotspot_activity_end_time; // End time of current hotspot becomes start time of next hotspot
                                else :

                                    //UPDATE STARTING LOCATION AND TIME DETAILS FOR NEXT ITERATION
                                    $after_activity_added_hotspot_start_time = $hotspot_activity_end_time;
                                endif;

                            endif;
                            $after_activity_added_hotspot_start_time = $after_activity_added_hotspot_end_time;
