            $start_location_id = getITINEARYROUTE_DETAILS($itinerary_plan_ID, $itinerary_route_ID, 'get_starting_location_id');

            //DELETE THE SELECTED HOTSPOT ACTIVITY FROM THE PARTICULAR ROUTE
            $delete_the_selected_hotspots_activity = sqlQUERY_LABEL("DELETE FROM `dvi_itinerary_route_activity_details` WHERE `deleted` = '0' and `status` = '1' and `itinerary_plan_ID` = '$itinerary_plan_ID' and `itinerary_route_ID` = '$itinerary_route_ID' and `hotspot_ID` = '$hotspot_ID' AND `activity_ID` = '$activity_ID'") or die("#1-UNABLE_TO_DELETE_ITINEARY_HOTSPOT_LIST:" . sqlERROR_LABEL());

            // DELETE THE PREVIOUSLY ADDED ALL THE HOTSPOTS ACTIVITY ENTRY COST
            $delete_hotspots_activity_entry_ticket_sqlwhere = " `itinerary_plan_id` = '$itinerary_plan_ID' AND `itinerary_route_ID` = '$itinerary_route_ID' and `hotspot_ID` = '$hotspot_ID' AND `activity_ID` = '$activity_ID' ";
            if (sqlACTIONS("DELETE", "dvi_itinerary_route_activity_entry_cost_details", '', '', $delete_hotspots_activity_entry_ticket_sqlwhere)): 
            endif;

            //DELETE THE SELECTED HOTSPOT ACTIVITY ENTRY COST FROM THE PARTICULAR ROUTE
            $delete_the_selected_hotspots_activity_entry_cost = sqlQUERY_LABEL("DELETE FROM `dvi_itinerary_route_activity_entry_cost_details` WHERE `deleted` = '0' and `status` = '1' and `itinerary_plan_id` = '$itinerary_plan_ID' and `itinerary_route_id` = '$itinerary_route_ID' and `hotspot_ID` = '$hotspot_ID' AND `activity_ID` = '$activity_ID'") or die("#1-UNABLE_TO_DELETE_ITINEARY_HOTSPOT_LIST:" . sqlERROR_LABEL());

            //COLLECT THE REMAINING HOTSPOT LIST TO REORDER AGAIN
            $select_remaining_hotspot_details = sqlQUERY_LABEL("SELECT HOTSPOT_ROUTE_DETAILS.`route_hotspot_ID`, HOTSPOT_ROUTE_DETAILS.`item_type`, HOTSPOT_ROUTE_DETAILS.`hotspot_order`, HOTSPOT_ROUTE_DETAILS.`hotspot_ID`, HOTSPOT_ROUTE_DETAILS.`hotspot_start_time`, HOTSPOT_PLACE.`hotspot_location`, HOTSPOT_PLACE.`hotspot_latitude`, HOTSPOT_PLACE.`hotspot_longitude`, HOTSPOT_PLACE.`hotspot_duration` FROM `dvi_itinerary_route_hotspot_details` HOTSPOT_ROUTE_DETAILS LEFT JOIN `dvi_hotspot_place` HOTSPOT_PLACE ON HOTSPOT_PLACE.`hotspot_ID` = HOTSPOT_ROUTE_DETAILS.`hotspot_ID` WHERE HOTSPOT_ROUTE_DETAILS.`deleted` = '0' and HOTSPOT_ROUTE_DETAILS.`status` = '1' and HOTSPOT_ROUTE_DETAILS.`item_type` NOT IN ('1','2','5','6') and HOTSPOT_ROUTE_DETAILS.`itinerary_plan_ID` = '$itinerary_plan_ID' and HOTSPOT_ROUTE_DETAILS.`itinerary_route_ID` = '$itinerary_route_ID' AND HOTSPOT_ROUTE_DETAILS.`route_hotspot_ID` >= '$route_hotspot_ID' ORDER BY HOTSPOT_ROUTE_DETAILS.`hotspot_order`, HOTSPOT_ROUTE_DETAILS.`route_hotspot_ID` ASC") or die("#1-UNABLE_TO_COLLECT_ITINEARY_HOTSPOT_LIST:" . sqlERROR_LABEL());
            $check_total_hotspot_num_rows_count = sqlNUMOFROW_LABEL($select_remaining_hotspot_details);

            $selected_hotspot_query_data = sqlQUERY_LABEL("SELECT HOTSPOT_PLACE.`hotspot_latitude`, HOTSPOT_PLACE.`hotspot_longitude`, ROUTE_HOTSPOT.`hotspot_start_time`, ROUTE_HOTSPOT.`hotspot_end_time`, ROUTE_HOTSPOT.`hotspot_traveling_time`, HOTSPOT_PLACE.`hotspot_location` FROM `dvi_itinerary_route_hotspot_details` ROUTE_HOTSPOT LEFT JOIN `dvi_hotspot_place` HOTSPOT_PLACE ON HOTSPOT_PLACE.`hotspot_ID` = ROUTE_HOTSPOT.`hotspot_ID` WHERE ROUTE_HOTSPOT.`status`='1' AND ROUTE_HOTSPOT.`deleted`='0' AND ROUTE_HOTSPOT.`itinerary_plan_ID` = '$itinerary_plan_ID' AND ROUTE_HOTSPOT.`itinerary_route_ID` = '$itinerary_route_ID' AND ROUTE_HOTSPOT.`item_type` = '4' AND ROUTE_HOTSPOT.`hotspot_ID` = '$hotspot_ID'") or die("#1-getITINEARY_ROUTE_HOTSPOT_DETAILS: " . sqlERROR_LABEL());
            if (sqlNUMOFROW_LABEL($selected_hotspot_query_data) > 0) :
                while ($fetch_hotspot_time_data = sqlFETCHARRAY_LABEL($selected_hotspot_query_data)) :
                    $staring_location_latitude = $fetch_hotspot_time_data['hotspot_latitude'];
                    $staring_location_longtitude = $fetch_hotspot_time_data['hotspot_longitude'];
                    $hotspot_start_time = $fetch_hotspot_time_data['hotspot_end_time'];
                    $start_hotspot_location = $fetch_hotspot_time_data['hotspot_location'];
                    $activity_hotspot_start_time = $fetch_hotspot_time_data['hotspot_start_time'];
                    $activity_hotspot_end_time = $fetch_hotspot_time_data['hotspot_end_time'];
                    $activity_hotspot_traveling_time = $fetch_hotspot_time_data['hotspot_traveling_time'];
                endwhile;
            endif;

            $previous_hotspot_location = $start_hotspot_location;

            $total_duration_seconds = 0;
            $hotspot_duration = "00:00:00";
            if ($check_total_hotspot_num_rows_count > 0) :
                while ($fetch_itinerary_remaining_hotspot_data = sqlFETCHARRAY_LABEL($select_remaining_hotspot_details)) :
                    $selected_route_hotspot_ID = $fetch_itinerary_remaining_hotspot_data['route_hotspot_ID'];
                    $item_type = $fetch_itinerary_remaining_hotspot_data['item_type'];
                    $hotspot_ID = $fetch_itinerary_remaining_hotspot_data['hotspot_ID'];
                    $hotspot_latitude = $fetch_itinerary_remaining_hotspot_data['hotspot_latitude'];
                    $hotspot_longitude = $fetch_itinerary_remaining_hotspot_data['hotspot_longitude'];
                    $hotspot_duration = $fetch_itinerary_remaining_hotspot_data['hotspot_duration'];
                    $end_hotspot_location = $fetch_itinerary_remaining_hotspot_data['hotspot_location'];

                    if ($hotspot_duration) :
                        $hotspot_duration = $hotspot_duration;
                    else :
                        $hotspot_duration = "00:00:00";
                    endif;

                    if ($item_type == 3) :
                        if ($selected_route_hotspot_ID != $route_hotspot_ID) :
                            // Determine the travel location type

                            $travel_location_type = getTravelLocationType($previous_hotspot_location, $end_hotspot_location);

                            $result = calculateDistanceAndDuration($staring_location_latitude, $staring_location_longtitude, $hotspot_latitude, $hotspot_longitude, $travel_location_type);
                            $hotspot_travelling_distance = number_format($result['distance'], 2, '.', '');
                            $hotspot_traveling_time = $result['duration'];

                            // EXTRACT THE HOURS AND MINUTES FROM THE DURATION STRING
                            preg_match('/(\d+) hour/', $hotspot_traveling_time, $hoursMatch);
                            preg_match('/(\d+) mins/', $hotspot_traveling_time, $minutesMatch);

                            // INITIALIZE HOURS AND MINUTES TO ZERO
                            $hours = 0;
                            $minutes = 0;

                            $hours = isset($hoursMatch[1]) ? intval($hoursMatch[1]) : 0;
                            $minutes = isset($minutesMatch[1]) ? intval($minutesMatch[1]) : 0;

                            // CALCULATE EXTRA HOURS IF MINUTES EXCEED 59
                            $extraHours = floor($minutes / 60);
                            $hours += $extraHours;
                            $minutes %= 60; // REMAINING MINUTES AFTER ADDING TO HOURS

                            // FORMAT HOURS AND MINUTES WITH LEADING ZEROS
                            $formattedHours = str_pad($hours, 2, '0', STR_PAD_LEFT);
                            $formattedMinutes = str_pad($minutes, 2, '0', STR_PAD_LEFT);

                            // FORMAT THE TIME AS H:i:s
                            $hotspot_duration = sprintf('%02d:%02d:00', $formattedHours, $formattedMinutes);

                            // Calculate the end time by adding the duration to the start time
                            $get_hotspot_duration_seconds = strtotime("1970-01-01 $hotspot_duration UTC");
                            $get_hotspot_ending_time_seconds = strtotime($hotspot_start_time) + $get_hotspot_duration_seconds;
                            //CONVERT THE NEW TIME TO {hotspot_siteseeing_end_time} H:i:s FORMAT
                            $hotspot_end_time = date('H:i:s', $get_hotspot_ending_time_seconds);
                            $itinerary_travel_type_buffer_time = "00:00:00";
                        endif;
                    elseif ($item_type == 4) :
                        if ($selected_route_hotspot_ID != $route_hotspot_ID) :
                            $hotspot_duration = $hotspot_duration;
                            // Calculate the end time by adding the duration to the start time
                            $get_hotspot_duration_seconds = strtotime("1970-01-01 $hotspot_duration UTC");
                            $get_hotspot_ending_time_seconds = strtotime($hotspot_start_time) + $get_hotspot_duration_seconds;
                            //CONVERT THE NEW TIME TO {hotspot_siteseeing_end_time} H:i:s FORMAT
