                            $hotspot_travelling_distance = 0;
                            $itinerary_travel_type_buffer_time = "00:00:00";
                        else:
                            $hotspot_start_time = $activity_hotspot_start_time;
                            $hotspot_end_time = $activity_hotspot_end_time;
                            $hotspot_duration = $activity_hotspot_traveling_time;
                            $itinerary_travel_type_buffer_time = "00:00:00";
                            $hotspot_travelling_distance = 0;
                        endif;
                    endif;

                    $arrFields = array('`hotspot_traveling_time`', '`itinerary_travel_type_buffer_time`', '`hotspot_travelling_distance`', '`hotspot_start_time`', '`hotspot_end_time`', '`hotspot_plan_own_way`', '`status`');

                    $arrValues = array("$hotspot_duration", "$itinerary_travel_type_buffer_time", "$hotspot_travelling_distance", "$hotspot_start_time", "$hotspot_end_time", "0", "1");

                    $sqlWhere = " `route_hotspot_ID` = '$selected_route_hotspot_ID' AND `item_type` = '$item_type' ";
                    //UPDATE HOTSPOT DETAILS
                    if (sqlACTIONS("UPDATE", "dvi_itinerary_route_hotspot_details", $arrFields, $arrValues, $sqlWhere)) :

                        if ($item_type == 4) :

                            $check_hotspot_activity_available = sqlQUERY_LABEL("SELECT `route_activity_ID`, `activity_ID` FROM `dvi_itinerary_route_activity_details` WHERE `status`='1' AND `deleted`='0' AND `itinerary_plan_ID` = '$itinerary_plan_ID' AND `itinerary_route_ID` = '$itinerary_route_ID' AND `hotspot_ID` = '$hotspot_ID' AND `route_hotspot_ID` = '$selected_route_hotspot_ID'") or die("#1-getITINEARY_ROUTE_HOTSPOT_DETAILS: " . sqlERROR_LABEL());
                            $total_hotspot_activity_num_rows_count = sqlNUMOFROW_LABEL($check_hotspot_activity_available);

                            if ($total_hotspot_activity_num_rows_count > 0) :
                                $activity_order = 1;
                                while ($fetch_itinerary_hotspot_activity_data = sqlFETCHARRAY_LABEL($check_hotspot_activity_available)) :
                                    $activity_order++;
                                    $route_activity_ID = $fetch_itinerary_hotspot_activity_data['route_activity_ID'];
                                    $activity_ID = $fetch_itinerary_hotspot_activity_data['activity_ID'];

                                    $select_hotspot_activity_details = sqlQUERY_LABEL("SELECT `activity_duration` FROM `dvi_activity` WHERE `deleted` = '0' AND `status` = '1' AND `hotspot_id` = '$hotspot_ID' AND `activity_id` = '$activity_ID'") or die("#1-UNABLE_TO_COLLECT_HOTSPOT_ACTIVITY_LIST:" . sqlERROR_LABEL());
                                    while ($fetch_hotspot_activity_data = sqlFETCHARRAY_LABEL($select_hotspot_activity_details)) :
                                        $activity_duration = $fetch_hotspot_activity_data['activity_duration'];
                                    endwhile;

                                    /* if ($entry_ticket_required == 1) : */
                                    $get_activity_charges_for_foreign_adult = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_foreign_adult');
                                    $get_activity_charges_for_foreign_children = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_foreign_children');
                                    $get_activity_charges_for_foreign_infant = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_foreign_infant');
                                    $get_activity_charges_for_adult = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_adult');
                                    $get_activity_charges_for_children = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_children');
                                    $get_activity_charges_for_infant = get_ITINEARY_HOTSPOT_ACTIVITY_COST_DETAILS($activity_ID, $itinerary_route_date, 'get_activity_charges_for_infant');

                                    if ($nationality != 101) :
                                        $get_activity_charges_for_adult = $get_activity_charges_for_foreign_adult;
                                        $get_activity_charges_for_children = $get_activity_charges_for_foreign_children;
                                        $get_activity_charges_for_infant = $get_activity_charges_for_foreign_infant;
                                    else :
                                        $get_activity_charges_for_adult = $get_activity_charges_for_adult;
                                        $get_activity_charges_for_children = $get_activity_charges_for_children;
                                        $get_activity_charges_for_infant = $get_activity_charges_for_infant;
                                    endif;

                                    $total_adult_activity_changes = $total_adult * $get_activity_charges_for_adult;
                                    $total_children_activity_changes = $total_children * $get_activity_charges_for_children;
                                    $total_infant_activity_changes = $total_infants * $get_activity_charges_for_infant;
                                    $total_activity_changes = $total_adult_activity_changes + $total_children_activity_changes + $total_infant_activity_changes;
                                    /* endif; */

                                    $hotspot_activity_start_time = $hotspot_end_time;

                                    // Convert start time to seconds
                                    list($start_hour, $start_minute, $start_second) = explode(":", $hotspot_activity_start_time);
                                    $start_time_seconds = $start_hour * 3600 + $start_minute * 60 + $start_second;

                                    // Convert duration to seconds
                                    list($duration_hour, $duration_minute, $duration_second) = explode(":", $activity_duration);
                                    $duration_seconds = $duration_hour * 3600 + $duration_minute * 60 + $duration_second;

                                    // Add duration to start time
                                    $total_time_seconds = $start_time_seconds + $duration_seconds;

                                    // Convert total time back to H:i:s format
                                    $hotspot_activity_end_time = gmdate("H:i:s", $total_time_seconds);

                                    $route_hotspot_activity_arrFields = array('`activity_order`', '`activity_charges_for_foreign_adult`', '`activity_charges_for_foreign_children`', '`activity_charges_for_foreign_infant`', '`activity_charges_for_adult`', '`activity_charges_for_children`', '`activity_charges_for_infant`', '`activity_amout`', '`activity_traveling_time`', '`activity_start_time`', '`activity_end_time`');
                                    $route_hotspot_activity_arrValues = array("$activity_order", "$get_activity_charges_for_foreign_adult", "$get_activity_charges_for_foreign_children", "$get_activity_charges_for_foreign_infant", "$get_activity_charges_for_adult", "$get_activity_charges_for_children", "$get_activity_charges_for_infant", "$total_activity_changes", "$activity_duration", "$hotspot_activity_start_time", "$hotspot_activity_end_time");
                                    $route_hotspot_activity_sqlwhere = " `itinerary_plan_ID` = '$itinerary_plan_ID' AND `itinerary_route_ID` = '$itinerary_route_ID' AND `route_hotspot_ID` = '$selected_route_hotspot_ID' AND `hotspot_ID` = '$hotspot_ID' AND `activity_ID` = '$activity_ID' ";

                                    //INSERT THE ITINEARY HOTSPOT ACTIVITY DATA
                                    if (sqlACTIONS("UPDATE", "dvi_itinerary_route_activity_details", $route_hotspot_activity_arrFields, $route_hotspot_activity_arrValues, $route_hotspot_activity_sqlwhere)) :
                                        $hotspot_activity_start_time = $hotspot_activity_end_time;
                                    endif;

                                endwhile;

                                //UPDATE STARTING LOCATION AND TIME DETAILS FOR NEXT ITERATION
                                $staring_location_latitude = $hotspot_latitude;
                                $staring_location_longtitude = $hotspot_longitude;
                                $hotspot_start_time = $hotspot_activity_end_time; // End time of current hotspot becomes start time of next hotspot
                                $previous_hotspot_location = $end_hotspot_location;

                            else :

                                //UPDATE STARTING LOCATION AND TIME DETAILS FOR NEXT ITERATION
                                $staring_location_latitude = $hotspot_latitude;
                                $staring_location_longtitude = $hotspot_longitude;
                                $hotspot_start_time = $hotspot_end_time; // End time of current hotspot becomes start time of next hotspot
                                $previous_hotspot_location = $end_hotspot_location;

                            endif;
                        else:
                            $hotspot_start_time = $hotspot_end_time;
                        endif;
                    endif;

                endwhile;
            else:
                $selected_after_activity_added_hotspot_query_data = sqlQUERY_LABEL("SELECT HOTSPOT.`hotspot_latitude`, HOTSPOT.`hotspot_longitude`, HOTSPOT.`hotspot_location` FROM `dvi_itinerary_route_hotspot_details` ROUTE_HOTSPOT LEFT JOIN `dvi_hotspot_place` HOTSPOT ON HOTSPOT.`hotspot_ID` = ROUTE_HOTSPOT.`hotspot_ID` WHERE ROUTE_HOTSPOT.`status`='1' AND ROUTE_HOTSPOT.`deleted`='0' AND ROUTE_HOTSPOT.`itinerary_plan_ID` = '$itinerary_plan_ID' AND ROUTE_HOTSPOT.`itinerary_route_ID` = '$itinerary_route_ID' AND ROUTE_HOTSPOT.`route_hotspot_ID` = '$route_hotspot_ID' AND ROUTE_HOTSPOT.`item_type` IN ('3','4') ORDER BY ROUTE_HOTSPOT.`hotspot_order`,ROUTE_HOTSPOT.`item_type` ASC") or die("#1-getITINEARY_ROUTE_HOTSPOT_DETAILS: " . sqlERROR_LABEL());
                if (sqlNUMOFROW_LABEL($selected_after_activity_added_hotspot_query_data) > 0) :
                    while ($fetch_hotspot_time_data = sqlFETCHARRAY_LABEL($selected_after_activity_added_hotspot_query_data)) :
                        $staring_location_latitude = $fetch_hotspot_time_data['hotspot_latitude'];
                        $staring_location_longtitude = $fetch_hotspot_time_data['hotspot_longitude'];
                        $previous_hotspot_location = $fetch_hotspot_time_data['hotspot_location'];
                    endwhile;
                endif;
            endif;

            $hotspot_start_time = $hotspot_end_time;

            $get_last_route_id_from_this_itinerary_plan = sqlQUERY_LABEL("SELECT MAX(`itinerary_route_ID`) AS max_route_id FROM `dvi_itinerary_route_details` WHERE `deleted` = '0' and `status` = '1' AND `itinerary_plan_ID` = '$itinerary_plan_ID' ORDER BY `itinerary_route_ID` DESC LIMIT 1") or die("#1-UNABLE_TO_COLLECT_ITINEARY_HOTSPOT_LIST:" . sqlERROR_LABEL());
            $fetch_last_route_id_from_this_itineary_plan = sqlFETCHARRAY_LABEL($get_last_route_id_from_this_itinerary_plan);
            $last_itinerary_route_ID = $fetch_last_route_id_from_this_itineary_plan['max_route_id'];

            //INSERT THE END OF THE TRIP DEPARTURE START TIME
            if (trim($departure_location) == trim($selected_NEXT_VISITING_PLACE) && $last_itinerary_route_ID == $itinerary_route_ID) :
                //Determine the buffer time based on the departure_type [1 - By Flight | 2 - By Train | 3 - By Road]
                switch ($departure_type):
                        case 1: // By Flight
                        $itinerary_travel_type_buffer_time = getGLOBALSETTING('itinerary_travel_by_flight_buffer_time');
                        break;
                    case 2: // By Train
                        $itinerary_travel_type_buffer_time = getGLOBALSETTING('itinerary_travel_by_train_buffer_time');
                        break;
                    case 3: // By Road
                        $itinerary_travel_type_buffer_time = getGLOBALSETTING('itinerary_travel_by_road_buffer_time');
                        break;
                    default:
                        $itinerary_travel_type_buffer_time = "00:00:00"; // Default to 0 if departure type is invalid
                endswitch;

                $ending_location_latitude = getSTOREDLOCATIONDETAILS($start_location_id, 'destination_location_lattitude');
                $ending_location_longtitude = getSTOREDLOCATIONDETAILS($start_location_id, 'destination_location_longitude');
                $ending_location_name = getSTOREDLOCATIONDETAILS($start_location_id, 'DESTINATION_LOCATION');

                // Determine the travel location type
                $travel_location_type = getTravelLocationType($previous_hotspot_location, $ending_location_name);

                // CALULATE THE DISTANCE AND DURATION TO THE END LOCATION
                $result = calculateDistanceAndDuration($staring_location_latitude, $staring_location_longtitude, $ending_location_latitude, $ending_location_longtitude, $travel_location_type);

                $destination_travelling_distance = number_format($result['distance'], 2, '.', '');
                $destination_traveling_time = $result['duration'];

                // EXTRACT THE HOURS AND MINUTES FROM THE DURATION STRING
                preg_match('/(\d+) hour/', $destination_traveling_time, $hoursMatch);
                preg_match('/(\d+) mins/', $destination_traveling_time, $minutesMatch);

                // INITIALIZE HOURS AND MINUTES TO ZERO
                $hours = 0;
                $minutes = 0;

                $hours = isset($hoursMatch[1]) ? intval($hoursMatch[1]) : 0;
                $minutes = isset($minutesMatch[1]) ? intval($minutesMatch[1]) : 0;

                // CALCULATE EXTRA HOURS IF MINUTES EXCEED 59
                $extraHours = floor($minutes / 60);
                $hours += $extraHours;
                $minutes %= 60; // REMAINING MINUTES AFTER ADDING TO HOURS

                // FORMAT HOURS AND MINUTES WITH LEADING ZEROS
                $formattedHours = str_pad($hours, 2, '0', STR_PAD_LEFT);
                $formattedMinutes = str_pad($minutes, 2, '0', STR_PAD_LEFT);

                // FORMAT THE TIME AS H:i:s
                $duration_formatted = sprintf('%02d:%02d:00', $formattedHours, $formattedMinutes);

                //CALCAULATE THE DURATIONS IN SECONDS
                $totalSeconds = ($hours * 3600) + ($minutes * 60);

                //ADD THE DURATION TO THE START TIME
                $newTimeInSeconds = $totalSeconds + strtotime($itinerary_travel_type_buffer_time);

                //CONVERT THE NEW TIME TO {destination_total_duration} H:i:s FORMAT
                $destination_total_duration = date('H:i:s', $newTimeInSeconds);

                // Convert hotspot_start_time to seconds
                $startTimeInSeconds = strtotime($hotspot_start_time);

                // Convert destination_total_duration to seconds
                list($hours, $minutes, $seconds) = sscanf($destination_total_duration, "%d:%d:%d");
                $durationInSeconds = $hours * 3600 + $minutes * 60 + $seconds;

                // Add the duration and buffer time to the start time
                $totalTimeInSeconds = $startTimeInSeconds + $durationInSeconds + $bufferInSeconds;

                // Convert the total time back to H:i:s format
                $destination_travel_end_time = date('H:i:s', $totalTimeInSeconds);

