                //MORE THAN ONE ROOM PER DAY
                if ($hotel_required == 1) :

                    // Step 1: Fetch hotel room data and store in an array
                    $select_hotel_room_data = sqlQUERY_LABEL("SELECT HOTEL.`hotel_category`, HOTEL.`hotel_margin`, HOTEL.`hotel_margin_gst_type`, HOTEL.`hotel_margin_gst_percentage`, HOTEL_ROOM.`room_ID`, HOTEL_ROOM.`room_type_id`, HOTEL_ROOM.`gst_type`, HOTEL_ROOM.`gst_percentage` FROM `dvi_hotel_rooms` HOTEL_ROOM LEFT JOIN `dvi_hotel` HOTEL ON HOTEL.`hotel_id` = HOTEL_ROOM.`hotel_id` WHERE HOTEL_ROOM.`deleted` = '0' AND HOTEL_ROOM.`hotel_id` = '$hotel_id'") or die("#3-UNABLE_TO_COLLECT_DETAILS:" . sqlERROR_LABEL());

