                    else :
                        //success call		
                        $response['success'] = true;

                        // Fetch hotel room details for the specific itinerary plan
                        $selected_hotel_room_query = sqlQUERY_LABEL("SELECT `itinerary_plan_hotel_room_details_ID`, `itinerary_plan_id`, `itinerary_route_id`, `itinerary_route_date`, `hotel_id`, `room_qty`, `total_breafast_cost`, `total_lunch_cost`, `total_dinner_cost`, `extra_bed_rate`, `child_without_bed_charges`, `child_with_bed_charges`, `total_room_cost`, `total_room_gst_amount`, `gst_type`, `gst_percentage` FROM `dvi_itinerary_plan_hotel_room_details` WHERE `deleted` = '0' AND `status` = '1' AND `itinerary_plan_id` = '$itinerary_plan_id' AND `itinerary_route_id` = '$itinerary_route_id' AND `group_type` = '$group_type'") or die("#STATELABEL-LABEL: getHOTEL_DETAIL: " . sqlERROR_LABEL());

