                            if ($total_hotel_meal_plan_cost > 0 && $gst_percentage > 0) :
                                // Calculate new margin amount and room tax amount based on GST type
                                if ($gst_type == 1) :
                                    // For Inclusive GST
                                    $total_hotel_meal_plan_cost_tax_amt = ((($total_hotel_meal_plan_cost) * $gst_percentage) / 100);
                                    $total_hotel_meal_plan_cost_amount = (($total_hotel_meal_plan_cost) - ($total_hotel_meal_plan_cost_tax_amt));
                                elseif ($gst_type == 2) :
                                    // For Exclusive GST
                                    $total_hotel_meal_plan_cost_tax_amt = ($total_hotel_meal_plan_cost * $gst_percentage / 100);
                                    $total_hotel_meal_plan_cost_amount = $total_hotel_meal_plan_cost;
                                endif;
                            else :
                                $total_hotel_meal_plan_cost_amount = $total_hotel_meal_plan_cost;
                                $total_hotel_meal_plan_cost_tax_amt = 0;
                            endif;

                            $total_hotel_cost = $total_breafast_cost_amount +  $total_lunch_cost_amount + $total_dinner_cost_amount + $total_room_cost +  $total_extra_bed_cost_amount + $child_with_bed_charges_amount +  $child_without_bed_charges_amount + $new_margin_amount;

                            $total_hotel_tax_amount = $total_breafast_cost_tax_amt + $total_lunch_cost_tax_amt + $total_dinner_cost_tax_amt + $new_room_tax_amt + $total_extra_bed_cost_tax_amt + $child_without_bed_charges_tax_amt + $child_with_bed_charges_tax_amt + $new_margin_tax_amt;

                            // Check if there is existing hotel details record for this date
                            $select_itinerary_plan_hotel_data = sqlQUERY_LABEL("SELECT `itinerary_plan_hotel_details_ID` FROM `dvi_itinerary_plan_hotel_details` WHERE `deleted` = '0' AND `itinerary_plan_id` = '$itinerary_plan_id' AND `itinerary_route_id` = '$itinerary_route_id' AND `group_type` = '$group_type'") or die("#3-UNABLE_TO_COLLECT_DETAILS:" . sqlERROR_LABEL());
                            $select_itinerary_plan_hotel_count = sqlNUMOFROW_LABEL($select_itinerary_plan_hotel_data);

                            // Prepare fields and values for insertion or update
                            $hotel_arrFields = array('`itinerary_plan_id`', '`itinerary_route_id`', '`itinerary_route_date`', '`itinerary_route_location`', '`hotel_required`', '`hotel_category_id`', '`hotel_id`', '`hotel_margin_percentage`', '`hotel_margin_gst_type`', '`hotel_margin_gst_percentage`', '`hotel_margin_rate`', '`hotel_margin_rate_tax_amt`', '`hotel_breakfast_cost`', '`hotel_breakfast_cost_gst_amount`', '`hotel_lunch_cost`', '`hotel_lunch_cost_gst_amount`', '`hotel_dinner_cost`', '`hotel_dinner_cost_gst_amount`', '`total_no_of_persons`', '`total_hotel_meal_plan_cost`', '`total_hotel_meal_plan_cost_gst_amount`', '`total_no_of_rooms`', '`total_room_cost`', '`total_room_gst_amount`', '`total_extra_bed_cost`', '`total_extra_bed_cost_gst_amount`', '`total_childwith_bed_cost`', '`total_childwith_bed_cost_gst_amount`', '`total_childwithout_bed_cost`', '`total_childwithout_bed_cost_gst_amount`', '`total_hotel_cost`', '`total_hotel_tax_amount`', '`total_amenities_cost`', '`total_amenities_gst_amount`', '`createdby`', '`status`');

                            $hotel_arrValues = array("$itinerary_plan_id", "$itinerary_route_id", "$itinerary_route_date", "$next_visiting_location", "$hotel_required", "$hotel_category", "$hotel_id", "$hotel_margin", "$hotel_margin_gst_type", "$hotel_margin_gst_percentage", "$new_margin_amount", "$new_margin_tax_amt", "$total_breafast_cost_amount", "$total_breafast_cost_tax_amt", "$total_lunch_cost_amount", "$total_lunch_cost_tax_amt", "$total_dinner_cost_amount", "$total_dinner_cost_tax_amt", "$total_no_of_persons", "$total_hotel_meal_plan_cost_amount", "$total_hotel_meal_plan_cost_tax_amt", "$room_qty", "$total_room_cost", "$new_room_tax_amt", "$total_extra_bed_cost_amount", "$total_extra_bed_cost_tax_amt", "$child_with_bed_charges_amount", "$child_with_bed_charges_tax_amt", "$child_without_bed_charges_amount", "$child_without_bed_charges_tax_amt", "$total_hotel_cost", "$total_hotel_tax_amount", "0", "0", "$logged_user_id", "1");

