                        $select_itineary_plan_hotel_room_data = sqlQUERY_LABEL("SELECT `itinerary_plan_hotel_room_details_ID`, `itinerary_plan_hotel_details_id` FROM `dvi_itinerary_plan_hotel_room_details` WHERE `deleted` = '0' and `itinerary_plan_id` = '$itinerary_plan_id' AND `itinerary_route_id` = '$itinerary_route_id' AND `deleted` = '0' AND `status` = '1' AND `group_type` = '$group_type'") or die("#3-UNABLE_TO_COLLECT_DETAILS:" . sqlERROR_LABEL());
