            else :
                //SINGLE ROOM PER DAY
                if ($hotel_required == 1) :
                    $select_itineary_plan_hotel_room_data = sqlQUERY_LABEL("SELECT `itinerary_plan_hotel_room_details_ID`, `extra_bed_count`, `child_without_bed_count`, `child_with_bed_count`, `breakfast_required`, `lunch_required`, `dinner_required`, `room_qty` FROM `dvi_itinerary_plan_hotel_room_details` WHERE `deleted` = '0' and `itinerary_plan_id` = '$itinerary_plan_id' AND `itinerary_route_id` = '$itinerary_route_id' AND `group_type` = '$group_type' AND `itinerary_plan_hotel_room_details_ID` = '$itinerary_plan_hotel_room_details_ID' AND `deleted` = '0' AND `status` = '1'") or die("#3-UNABLE_TO_COLLECT_DETAILS:" . sqlERROR_LABEL());
                    $total_num_of_room_allotted = sqlNUMOFROW_LABEL($select_itineary_plan_hotel_room_data);
                    while ($fetch_itineary_plan_hotel_room_data = sqlFETCHARRAY_LABEL($select_itineary_plan_hotel_room_data)) :
                        $itinerary_plan_hotel_room_details_ID = $fetch_itineary_plan_hotel_room_data['itinerary_plan_hotel_room_details_ID'];
                        $extra_bed_count = $fetch_itineary_plan_hotel_room_data['extra_bed_count'];
                        $child_without_bed_count = $fetch_itineary_plan_hotel_room_data['child_without_bed_count'];
                        $child_with_bed_count = $fetch_itineary_plan_hotel_room_data['child_with_bed_count'];
                        $breakfast_required = $fetch_itineary_plan_hotel_room_data['breakfast_required'];
                        $lunch_required = $fetch_itineary_plan_hotel_room_data['lunch_required'];
                        $dinner_required = $fetch_itineary_plan_hotel_room_data['dinner_required'];
                        $room_qty = $fetch_itineary_plan_hotel_room_data['room_qty'];
                    endwhile;
                    
                    $select_hotel_room_data = sqlQUERY_LABEL("SELECT HOTEL.`hotel_category`, HOTEL.`hotel_margin`, HOTEL.`hotel_margin_gst_type`, HOTEL.`hotel_margin_gst_percentage`, HOTEL_ROOM.`room_ID`, HOTEL_ROOM.`gst_type`, HOTEL_ROOM.`gst_percentage` FROM `dvi_hotel_rooms` HOTEL_ROOM LEFT JOIN `dvi_hotel` HOTEL ON HOTEL.`hotel_id` = HOTEL_ROOM.`hotel_id` LEFT JOIN `dvi_itinerary_plan_hotel_room_details` ITINEARY_PLAN_HOTEL_ROOM_DETAILS ON HOTEL.hotel_id = ITINEARY_PLAN_HOTEL_ROOM_DETAILS.`hotel_id` AND HOTEL_ROOM.`room_ID` = ITINEARY_PLAN_HOTEL_ROOM_DETAILS.`room_id` AND ITINEARY_PLAN_HOTEL_ROOM_DETAILS.`group_type` = '$group_type' AND ITINEARY_PLAN_HOTEL_ROOM_DETAILS.room_id IS NULL WHERE HOTEL_ROOM.`deleted` = '0' AND HOTEL_ROOM.`room_type_id` = '$choosen_room_type' AND HOTEL_ROOM.`hotel_id` = '$hotel_id'") or die("#3-UNABLE_TO_COLLECT_DETAILS:" . sqlERROR_LABEL());
                    while ($fetch_hotel_room_data = sqlFETCHARRAY_LABEL($select_hotel_room_data)) :
