        <script>
            function confirmMODIFYITINEARYHOTELROOMTYPE(group_type, itinerary_plan_hotel_details_ID, itinerary_plan_hotel_room_details_ID, itinerary_plan_id, itinerary_route_id, hotel_id, choosen_room_type, all_meal_plan, breakfast_meal_plan, lunch_meal_plan, dinner_meal_plan) {
                $.ajax({
                    type: 'post',
                    url: 'engine/ajax/ajax_latest_manage_itineary.php?type=confirm_modify_itineary_plan_hotel_room_type',
                    data: {
                        itinerary_plan_hotel_details_ID: itinerary_plan_hotel_details_ID,
                        itinerary_plan_hotel_room_details_ID: itinerary_plan_hotel_room_details_ID,
                        itinerary_plan_id: itinerary_plan_id,
                        itinerary_route_id: itinerary_route_id,
                        group_type: group_type,
                        hotel_id: hotel_id,
                        choosen_room_type: choosen_room_type,
                        all_meal_plan: all_meal_plan,
                        breakfast_meal_plan: breakfast_meal_plan,
                        lunch_meal_plan: lunch_meal_plan,
                        dinner_meal_plan: dinner_meal_plan,
                    },
                    dataType: 'json',
                    success: function(response) {
                        if (!response.success) {
                            // NOT SUCCESS RESPONSE
                            if (response.errors.something_went_wrong) {
                                TOAST_NOTIFICATION('warning', response.errors.something_went_wrong, 'Warning !!!', '', '', '', '', '', '', '', '', '');
                            } else if (response.errors.choosen_roomtype_pricebook_not_found) {
                                TOAST_NOTIFICATION('warning', response.errors.choosen_roomtype_pricebook_not_found, 'Warning !!!', '', '', '', '', '', '', '', '', '');
                            } else if (response.errors.choosen_roomtype_rooms_not_available) {
                                TOAST_NOTIFICATION('warning', response.errors.choosen_roomtype_rooms_not_available, 'Warning !!!', '', '', '', '', '', '', '', '', '');
                            }
                        } else {
                            // SUCCESS RESPONSE
                            TOAST_NOTIFICATION('success', 'Successfully Hotel Type Updated !!!', 'Success !!!', '', '', '', '', '', '', '', '', '');
                            $('#close_hotel_modify').click();
                            /* $('#close_multiple_room_modal').click(); */
                            showHOTELTAB(itinerary_plan_id, group_type);
                        }
                    }
                });
            }

            function showHOTELTAB(itinerary_plan_id, group_type) {
                var spinner = $('#spinner');
                // Remove active class from all nav-link buttons
                let tabs = document.querySelectorAll('.nav-link');
                tabs.forEach(function(tab) {
                    tab.classList.remove('active'); // Assuming 'active' is the class you want to remove
                });

                // Add active class to the clicked button
                let currentTab = document.getElementById('group_tab_' + group_type);
                let arrowgroupcurrentTab = document.getElementById('arrow_group_tab_' + group_type);
                currentTab.classList.add('active');
                arrowgroupcurrentTab.classList.add('active');
                spinner.show();
                $.ajax({
                    type: "POST",
                    url: "engine/ajax/ajax_show_recommended_hotel_details_form.php?type=show_form",
                    data: {
                        _planID: itinerary_plan_id,
                        _groupTYPE: group_type,
                    },
                    success: function(response) {
                        $('#show_recommended_hotel_details').html('')
                        $('#show_recommended_hotel_details').html(response);
                        showOVERALL_COST_DETAILS(itinerary_plan_id,group_type);
                        showOVERALLCOSTAMOUNT(itinerary_plan_id, group_type);
                        spinner.hide();
                    }
                });
            }

            function showOVERALLCOSTAMOUNT(itinerary_plan_ID, group_type) {
                $.ajax({
                    type: "POST",
                    url: "engine/ajax/ajax_latest_itineary_overall_cost_details.php?type=show_grand_itineary_total",
                    data: {
                        _itinerary_plan_ID: itinerary_plan_ID,
                        _groupTYPE: group_type,
                    },
                    success: function(response) {
                        $('#overall_trip_cost').text('');
                        $('#overall_trip_cost').text(response);
                    }
                });
            }

            function showOVERALL_COST_DETAILS(itinerary_plan_ID,group_type) {
                $.ajax({
                    type: "POST",
                    url: "engine/ajax/ajax_latest_itineary_overall_cost_details.php?type=show_form",
                    data: {
                        _itinerary_plan_ID: itinerary_plan_ID,
                        _groupTYPE: group_type,
                    },
                    success: function(response) {
                        $('#showOVERALLCOSTINFO').html('');
                        $('#showOVERALLCOSTINFO').html(response);
                    }
                });
            }
        </script>
    <?php
    elseif ($_GET['type'] == 'confirm_modify_itineary_plan_hotel_room_type') :

        $errors = [];
        $response = [];

        $itinerary_plan_hotel_details_ID = $_POST['itinerary_plan_hotel_details_ID'];
        $itinerary_plan_hotel_room_details_ID = $_POST['itinerary_plan_hotel_room_details_ID'];
        $itinerary_plan_id = $_POST['itinerary_plan_id'];
        $itinerary_route_id = $_POST['itinerary_route_id'];
        $group_type = $_POST['group_type'];
        $hotel_id = $_POST['hotel_id'];
        $choosen_room_type = $_POST['choosen_room_type'];
        $all_meal_plan = $_POST['all_meal_plan'];
        $breakfast_meal_plan = $_POST['breakfast_meal_plan'];
        $lunch_meal_plan = $_POST['lunch_meal_plan'];
        $dinner_meal_plan = $_POST['dinner_meal_plan'];
        $itinerary_route_date = getITINEARYROUTE_DETAILS($itinerary_plan_id, $itinerary_route_id, 'itinerary_route_date');
        $preferred_room_count = get_ITINERARY_PLAN_DETAILS($itinerary_plan_id, 'preferred_room_count');
        
        if (empty($itinerary_plan_hotel_details_ID) || empty($itinerary_plan_id) || empty($itinerary_route_id) || empty($hotel_id)) :
            $errors['something_went_wrong'] = "Something went wrong. Please try again Later...";
        endif;

        if (!empty($errors)) :
            //error call
            $response['success'] = false;
            $response['errors'] = $errors;
        else :

            $select_itineary_plan_hotel_room_data = sqlQUERY_LABEL("SELECT `itinerary_plan_hotel_room_details_ID`, `extra_bed_count`, `child_without_bed_count`, `child_with_bed_count`, `breakfast_required`, `lunch_required`, `dinner_required`, `room_qty` FROM `dvi_itinerary_plan_hotel_room_details` WHERE `deleted` = '0' and `itinerary_plan_id` = '$itinerary_plan_id' AND `itinerary_route_id` = '$itinerary_route_id' AND `group_type` = '$group_type' AND `itinerary_plan_hotel_room_details_ID` = '$itinerary_plan_hotel_room_details_ID' AND `deleted` = '0' AND `status` = '1'") or die("#3-UNABLE_TO_COLLECT_DETAILS:" . sqlERROR_LABEL());
            $total_num_of_room_allotted = sqlNUMOFROW_LABEL($select_itineary_plan_hotel_room_data);
            while ($fetch_itineary_plan_hotel_room_data = sqlFETCHARRAY_LABEL($select_itineary_plan_hotel_room_data)) :
                $itinerary_plan_hotel_room_details_ID = $fetch_itineary_plan_hotel_room_data['itinerary_plan_hotel_room_details_ID'];
                $extra_bed_count = $fetch_itineary_plan_hotel_room_data['extra_bed_count'];
                $child_without_bed_count = $fetch_itineary_plan_hotel_room_data['child_without_bed_count'];
                $child_with_bed_count = $fetch_itineary_plan_hotel_room_data['child_with_bed_count'];
                $breakfast_required = $fetch_itineary_plan_hotel_room_data['breakfast_required'];
                $lunch_required = $fetch_itineary_plan_hotel_room_data['lunch_required'];
                $dinner_required = $fetch_itineary_plan_hotel_room_data['dinner_required'];
                $room_qty = $fetch_itineary_plan_hotel_room_data['room_qty'];
            endwhile;

            $select_hotel_room_data = sqlQUERY_LABEL("SELECT HOTEL.`hotel_category`, HOTEL.`hotel_margin`, HOTEL.`hotel_margin_gst_type`, HOTEL.`hotel_margin_gst_percentage`, HOTEL_ROOM.`room_ID`, HOTEL_ROOM.`gst_type`, HOTEL_ROOM.`gst_percentage` FROM `dvi_hotel_rooms` HOTEL_ROOM LEFT JOIN `dvi_hotel` HOTEL ON HOTEL.`hotel_id` = HOTEL_ROOM.`hotel_id` LEFT JOIN `dvi_itinerary_plan_hotel_room_details` ITINEARY_PLAN_HOTEL_ROOM_DETAILS ON HOTEL.hotel_id = ITINEARY_PLAN_HOTEL_ROOM_DETAILS.`hotel_id` AND HOTEL_ROOM.`room_ID` = ITINEARY_PLAN_HOTEL_ROOM_DETAILS.`room_id` AND HOTEL_ROOM.`no_of_rooms_available` = ITINEARY_PLAN_HOTEL_ROOM_DETAILS.`room_qty` AND ITINEARY_PLAN_HOTEL_ROOM_DETAILS.`group_type` = '$group_type' AND ITINEARY_PLAN_HOTEL_ROOM_DETAILS.room_id IS NULL WHERE HOTEL_ROOM.`deleted` = '0' AND HOTEL_ROOM.`room_type_id` = '$choosen_room_type' AND HOTEL_ROOM.`hotel_id` = '$hotel_id'") or die("#3-UNABLE_TO_COLLECT_DETAILS:" . sqlERROR_LABEL());
            $get_total_room_count_availability = sqlNUMOFROW_LABEL($select_hotel_room_data);
            while ($fetch_hotel_room_data = sqlFETCHARRAY_LABEL($select_hotel_room_data)) :
                $hotel_category = $fetch_hotel_room_data['hotel_category'];
                $hotel_margin = $fetch_hotel_room_data['hotel_margin'];
                $hotel_margin_gst_type = $fetch_hotel_room_data['hotel_margin_gst_type'];
                $hotel_margin_gst_percentage = $fetch_hotel_room_data['hotel_margin_gst_percentage'];
                $room_ID = $fetch_hotel_room_data['room_ID'];
                $gst_type = $fetch_hotel_room_data['gst_type'];
                $gst_percentage = $fetch_hotel_room_data['gst_percentage'];
            endwhile;

            $pricebook_year = date('Y', strtotime($itinerary_route_date));
            $pricebook_month = date('F', strtotime($itinerary_route_date));
            $formatted_day = 'day_' . date('j', strtotime($itinerary_route_date));

            $hotel_breafast_cost = getHOTELMEAL_PRICEBOOK_DETAILS($hotel_id, $pricebook_year, $pricebook_month, $formatted_day, 'meal_rate_for_the_day', '1');
            $hotel_lunch_cost = getHOTELMEAL_PRICEBOOK_DETAILS($hotel_id, $pricebook_year, $pricebook_month, $formatted_day, 'meal_rate_for_the_day', '2');
            $hotel_dinner_cost = getHOTELMEAL_PRICEBOOK_DETAILS($hotel_id, $pricebook_year, $pricebook_month, $formatted_day, 'meal_rate_for_the_day', '3');

            $extra_bed_charge = getROOMBED_PRICEBOOK_DETAILS_WITH_ROOMTYPE($hotel_id, $room_ID,$choosen_room_type, $pricebook_year, $pricebook_month, $formatted_day, 'room_bed_rate_for_the_day', '1');
            $child_with_bed_charge = getROOMBED_PRICEBOOK_DETAILS_WITH_ROOMTYPE($hotel_id, $room_ID,$choosen_room_type, $pricebook_year, $pricebook_month, $formatted_day, 'room_bed_rate_for_the_day', '2');
            $child_without_bed_charge = getROOMBED_PRICEBOOK_DETAILS_WITH_ROOMTYPE($hotel_id, $room_ID,$choosen_room_type, $pricebook_year, $pricebook_month, $formatted_day, 'room_bed_rate_for_the_day', '3');

            $price_per_night = getROOM_PRICEBOOK_DETAILS_WITH_ROOMTYPE($hotel_id, $room_ID, $pricebook_year, $pricebook_month, $formatted_day, $choosen_room_type, 'room_rate_for_the_day');

            if ($get_total_room_count_availability == 0) :
                $errors['choosen_roomtype_rooms_not_available'] = "Choosen Roomtype Rooms Already Selected. Don't have Extra Room in this Roomtype";
            endif;

            if ($price_per_night > 0) :
            //ROOM SELECTION ALLOWED
            else :
                $errors['choosen_roomtype_pricebook_not_found'] = "Choosen Roomtype Pricebook Entry not found";
            endif;

            if (!empty($errors)) :
                //error call
                $response['success'] = false;
                $response['errors'] = $errors;
            else :
                $food_required_count = get_ITINERARY_PLAN_DETAILS($itinerary_plan_id, 'total_adult_n_children_count');
                $preferred_room_count = get_ITINERARY_PLAN_DETAILS($itinerary_plan_id, 'preferred_room_count');
                $total_extra_bed = $extra_bed_count;
                $total_child_without_bed = $child_without_bed_count;
                $total_child_with_bed = $child_with_bed_count;

                if ($preferred_room_count > 1) {
                    $food_required_count = ($food_required_count / $preferred_room_count);
                } else {
                    $food_required_count = $food_required_count;
                }

                $next_visiting_location = getITINEARYROUTE_DETAILS($itinerary_plan_id, $itinerary_route_id, 'next_visiting_location');

                if (!empty($all_meal_plan) || !empty($breakfast_meal_plan) || !empty($lunch_meal_plan) || !empty($dinner_meal_plan)) :
                    if ($all_meal_plan) :
                        $total_breafast_cost = (($food_required_count * $hotel_breafast_cost) * $room_qty);
                        $total_lunch_cost = (($food_required_count * $hotel_lunch_cost) * $room_qty);
                        $total_dinner_cost = (($food_required_count * $hotel_dinner_cost) * $room_qty);
                        $breakfast_meal_plan = 1;
                        $lunch_meal_plan = 1;
                        $dinner_meal_plan = 1;
                    else :
                        $total_breafast_cost = ($breakfast_meal_plan) ? (($food_required_count * $hotel_breafast_cost) * $room_qty) : 0;
                        $total_lunch_cost = ($lunch_meal_plan) ? (($food_required_count * $hotel_lunch_cost) * $room_qty) : 0;
                        $total_dinner_cost = ($dinner_meal_plan) ? (($food_required_count * $hotel_dinner_cost) * $room_qty) : 0;
                        $breakfast_meal_plan = ($breakfast_meal_plan) ? 1 : 0;
                        $lunch_meal_plan = ($lunch_meal_plan) ? 1 : 0;
                        $dinner_meal_plan = ($dinner_meal_plan) ? 1 : 0;
                    endif;
                else :
                    $total_breafast_cost = ($breakfast_required == 1) ? (($food_required_count * $hotel_breafast_cost) * $room_qty) : 0;
                    $total_lunch_cost = ($lunch_required == 1) ? (($food_required_count * $hotel_lunch_cost) * $room_qty) : 0;
                    $total_dinner_cost = ($dinner_required == 1) ? (($food_required_count * $hotel_dinner_cost) * $room_qty) : 0;
                    $breakfast_meal_plan = ($breakfast_required) ? 1 : 0;
                    $lunch_meal_plan = ($lunch_required) ? 1 : 0;
                    $dinner_meal_plan = ($breakfast_required) ? 1 : 0;
                endif;

                if ($total_extra_bed > 0) :
                    $extra_bed_charge = $extra_bed_charge * $total_extra_bed;
                else :
                    $extra_bed_charge = 0;
                endif;

                if ($total_child_without_bed > 0) :
                    $child_without_bed_charge = $child_without_bed_charge * $total_child_without_bed;
                else :
                    $child_without_bed_charge = 0;
                endif;

                if ($total_child_with_bed > 0) :
                    $child_with_bed_charge = $child_with_bed_charge * $total_child_with_bed;
                else :
