            while ($fetch_itinerary_plan_data = sqlFETCHARRAY_LABEL($select_itinerary_plan_details_query)) :
                $arrival_location = $fetch_itinerary_plan_data['arrival_location'];
                $departure_location = $fetch_itinerary_plan_data['departure_location'];
                $itinerary_quote_ID = $fetch_itinerary_plan_data['itinerary_quote_ID'];
                $trip_start_date_and_time = $fetch_itinerary_plan_data['trip_start_date_and_time'];
                $trip_end_date_and_time = $fetch_itinerary_plan_data['trip_end_date_and_time'];
                $location_id = $fetch_itinerary_plan_data['location_id'];
                $arrival_type = $fetch_itinerary_plan_data['arrival_type'];
                $departure_type = $fetch_itinerary_plan_data['departure_type'];
                $expecting_budget = $fetch_itinerary_plan_data['expecting_budget'];
                $itinerary_type = $fetch_itinerary_plan_data['itinerary_type'];
                $entry_ticket_required = $fetch_itinerary_plan_data['entry_ticket_required'];
                $no_of_routes = $fetch_itinerary_plan_data['no_of_routes'];
                $no_of_days = $fetch_itinerary_plan_data['no_of_days'];
                $no_of_nights = $fetch_itinerary_plan_data['no_of_nights'];
                $total_adult = $fetch_itinerary_plan_data['total_adult'];
                $total_children = $fetch_itinerary_plan_data['total_children'];
                $total_infants = $fetch_itinerary_plan_data['total_infants'];
                $nationality = $fetch_itinerary_plan_data['nationality'];
                $itinerary_preference = $fetch_itinerary_plan_data['itinerary_preference'];
                $meal_plan_breakfast = $fetch_itinerary_plan_data['meal_plan_breakfast'];
                $meal_plan_lunch = $fetch_itinerary_plan_data['meal_plan_lunch'];
                $meal_plan_dinner = $fetch_itinerary_plan_data['meal_plan_dinner'];
                $preferred_room_count = $fetch_itinerary_plan_data['preferred_room_count'];
                $total_extra_bed = $fetch_itinerary_plan_data['total_extra_bed'];
                $total_child_with_bed = $fetch_itinerary_plan_data['total_child_with_bed'];
                $total_child_without_bed = $fetch_itinerary_plan_data['total_child_without_bed'];
                $guide_for_itinerary = $fetch_itinerary_plan_data['guide_for_itinerary'];
                $food_type = $fetch_itinerary_plan_data['food_type'];
                $special_instructions = $fetch_itinerary_plan_data['special_instructions'];
                $pick_up_date_and_time = $fetch_itinerary_plan_data['pick_up_date_and_time'];
                $hotel_rates_visibility = $fetch_itinerary_plan_data['hotel_rates_visibility'];
            endwhile;
            $total_pax_count = $total_adult + $total_children + $total_infants;
        endif;

        $total_hotspot_amount = getITINEARY_COST_DETAILS($itinerary_plan_ID, '', 'total_hotspot_amount');
        $total_activity_amout = getITINEARY_COST_DETAILS($itinerary_plan_ID, '', 'total_activity_amout');
        $TOTAL_ITINEARY_GUIDE_CHARGES = getITINEARY_TOTAL_GUIDE_CHARGES_DETAILS('', $itinerary_plan_ID, '', 'TOTAL_ITINEARY_GUIDE_CHARGES');
        $total_hotel_amount = getITINEARY_COST_DETAILS($itinerary_plan_ID, $hotel_group_type, 'total_hotel_amount');
        $total_vehicle_amount = getITINEARY_COST_DETAILS($itinerary_plan_ID, '', 'total_vehicle_amount');
        $itinerary_sub_total = getITINEARY_COST_DETAILS($itinerary_plan_ID, $hotel_group_type, 'itineary_gross_total_amount') + $TOTAL_ITINEARY_GUIDE_CHARGES;

         $select_agent_details_query = sqlQUERY_LABEL("SELECT `agent_ID`, `itinerary_margin_discount_percentage`, `agent_margin`, `agent_margin_gst_type`, `agent_margin_gst_percentage` FROM `dvi_agent` WHERE `deleted` = '0' and `agent_ID` = '$agent_ID'") or die("#1-UNABLE_TO_COLLECT_ITINEARY_LIST:" . sqlERROR_LABEL());
        $total_agent_details_count = sqlNUMOFROW_LABEL($select_agent_details_query);
        if ($total_agent_details_count > 0) :
            while ($fetch_agent_data = sqlFETCHARRAY_LABEL($select_agent_details_query)) :
                $itinerary_margin_discount_percentage = $fetch_agent_data['itinerary_margin_discount_percentage'];
                $agent_margin = $fetch_agent_data['agent_margin'];
                $agent_margin_gst_type = $fetch_agent_data['agent_margin_gst_type'];
                $agent_margin_gst_percentage = $fetch_agent_data['agent_margin_gst_percentage'];
            endwhile;
        endif;
      
        $total_net_charge = round(getITINEARY_COST_DETAILS($itinerary_plan_ID, $hotel_group_type, 'itineary_gross_total_amount')) + round($TOTAL_ITINEARY_GUIDE_CHARGES);
        $itineary_gross_total_amount = getITINEARY_COST_DETAILS($itinerary_plan_ID, $hotel_group_type, 'itineary_gross_total_amount')+$TOTAL_ITINEARY_GUIDE_CHARGES;
        $payable_amount = $itineary_gross_total_amount ;

        // if ($agent_margin_gst_type == 1):
        //     $agent_margin_gst_label = 'Inclusive';
        //     $get_agent_margin = ($total_net_charge * $agent_margin) / 100;
        //     $gst_pecentage = ($get_agent_margin * $agent_margin_gst_percentage) / 100;
        //     $total_agent_margin =  $get_agent_margin -  $gst_pecentage;
        //     $total_net_amount = $total_net_charge + $total_agent_margin + $gst_pecentage;
        // else:
        //     $agent_margin_gst_label = 'Exclusive';
        //     $total_agent_margin = ($total_net_charge * $agent_margin) / 100;
        //     $gst_pecentage = ($total_agent_margin * $agent_margin_gst_percentage) / 100;
        //     $total_net_amount = $total_net_charge + $total_agent_margin + $gst_pecentage;
        // endif;
        
        $getguide = getINCIDENTALEXPENSES_LATESTITINERARY($itinerary_plan_ID, 'getguide');
        $gethotspot = getINCIDENTALEXPENSES_LATESTITINERARY($itinerary_plan_ID, 'gethotspot');
        $getactivity = getINCIDENTALEXPENSES_LATESTITINERARY($itinerary_plan_ID, 'getactivity');

        $incident_count = $getguide + $gethotspot + $getactivity;

        if ($agent_margin_gst_type == 1):
           
            $get_agent_margin = ($total_net_charge * $agent_margin) / 100;

            if ($incident_count == 0):
                $gst_pecentage =  0;
                $total_agent_margin =  0;
                $agent_margin_gst_percentage = 0;
                $agent_margin_gst_label = '--';
            else:
                $agent_margin_gst_label = 'Inclusive';
                $gst_pecentage = ($get_agent_margin * $agent_margin_gst_percentage) / 100;
                $total_agent_margin =  $get_agent_margin -  $gst_pecentage;
            endif;
            $total_net_amount = $total_net_charge + $total_agent_margin + $gst_pecentage;
        else:
            
            if ($incident_count == 0):
                $total_agent_margin =  0;
                $gst_pecentage = 0;
                $agent_margin_gst_percentage = 0;
                $agent_margin_gst_label = '--';
            else:
                $agent_margin_gst_label = 'Exclusive';
                $total_agent_margin = ($total_net_charge * $agent_margin) / 100;
                $gst_pecentage = ($total_agent_margin * $agent_margin_gst_percentage) / 100;
            endif;
            
            $total_net_amount = $total_net_charge + $total_agent_margin + $gst_pecentage;
        endif;

        if((in_array($itinerary_preference, array(1, 3)))):
            $select_hotel_details_query = sqlQUERY_LABEL("SELECT `itinerary_plan_hotel_details_ID`, `group_type`, `itinerary_plan_id`, `hotel_margin_rate` FROM `dvi_itinerary_plan_hotel_details` WHERE `group_type` = '$hotel_group_type' AND `itinerary_plan_id` = '$itinerary_plan_ID'") or die("#1-UNABLE_TO_COLLECT_ITINEARY_LIST:" . sqlERROR_LABEL());
            $total_hotel_details_count = sqlNUMOFROW_LABEL($select_hotel_details_query);
            if ($total_hotel_details_count > 0) :
                while ($fetch_agent_data = sqlFETCHARRAY_LABEL($select_hotel_details_query)) :
                    $hotel_margin_rate = $fetch_agent_data['hotel_margin_rate'];
                    $total_hotel_margin += $hotel_margin_rate;
                endwhile;
            endif;
        endif;

        $select_vehicle_details_query = sqlQUERY_LABEL("SELECT `itinerary_plan_vendor_eligible_ID`, `itineary_plan_assigned_status`, `itinerary_plan_id`, `vendor_margin_amount`, `total_vehicle_qty` FROM `dvi_itinerary_plan_vendor_eligible_list` WHERE `itineary_plan_assigned_status` = '1' AND `itinerary_plan_id` = '$itinerary_plan_ID'") or die("#1-UNABLE_TO_COLLECT_ITINEARY_LIST:" . sqlERROR_LABEL());
        $total_vehicle_details_count = sqlNUMOFROW_LABEL($select_vehicle_details_query);
        if ($total_vehicle_details_count > 0) :
            while ($fetch_vendor_data = sqlFETCHARRAY_LABEL($select_vehicle_details_query)) :
                $total_vehicle_qty = $fetch_vendor_data['total_vehicle_qty'];
                $vendor_margin_amount = $fetch_vendor_data['vendor_margin_amount'];
                $total_vehicle_margin += $vendor_margin_amount * $total_vehicle_qty;
            endwhile;
        endif;

        $total_margin_without_percentage = $total_agent_margin + $total_hotel_margin + $total_vehicle_margin;
        $total_margin_discount = ($total_margin_without_percentage * $itinerary_margin_discount_percentage) / 100;
        $total_discount_amount = $total_net_amount - $total_margin_discount;
        $itinerary_total_net_payable_amount = round($total_discount_amount);
        $itinerary_total_paid_amount = $itinerary_total_net_payable_amount*(ITINERARY_AGENT_CONFIRMATION_PAYMENT_PERCENTAGE/100);
        $itinerary_total_paid_amount = round($itinerary_total_paid_amount);
        $itinerary_total_balance_amount= $itinerary_total_net_payable_amount - $itinerary_total_paid_amount;

        //CHECK HOTEL ROOM SOLD OUT
        $check_hotel_room_sold_out = sqlQUERY_LABEL("SELECT ITINEARY_ROUTE.`itinerary_route_date` AS `route_date`, ITINEARY_ROUTE.`next_visiting_location` AS `location_name` FROM `dvi_itinerary_plan_hotel_details` AS ITINEARY_PLAN LEFT JOIN `dvi_itinerary_route_details` AS ITINEARY_ROUTE ON ITINEARY_PLAN.`itinerary_route_id` = ITINEARY_ROUTE.`itinerary_route_ID` WHERE ITINEARY_PLAN.`itinerary_plan_id` = '$itinerary_plan_ID' AND ITINEARY_PLAN.`group_type` = '$hotel_group_type' AND ITINEARY_PLAN.`total_room_cost` = '0' AND ITINEARY_PLAN.`deleted` = '0' AND ITINEARY_ROUTE.`deleted` = '0'") or die("#3-UNABLE_TO_COLLECT_DETAILS:" . sqlERROR_LABEL());

        $check_hotel_room_sold_out_count = sqlNUMOFROW_LABEL($check_hotel_room_sold_out);

        // Initialize variables
        $price_difference_hotel_sold_out_details = '';

        if ($check_hotel_room_sold_out_count > 0): 
            // Alert message
            $price_difference_hotel_sold_out_details .= '
                <div class="alert alert-danger alert-dismissible alert-label-icon label-arrow fade show" role="alert" style="margin-bottom: 20px;">
                    <i class="ri-alert-line label-icon"></i>
                    <strong>Some days in the itinerary do not have hotel bookings. Please ensure that all days have hotel bookings.</strong>
                </div>
                <div class="table-responsive mb-3">
                    <table class="table table-hover table-bordered table-nowrap align-middle mb-0">
                        <thead>
                            <tr class="text-muted text-uppercase">
                                <th scope="col">Sl No</th>
                                <th scope="col">Date</th>
                                <th scope="col">Location</th>
                            </tr>
                        </thead>
                        <tbody>';

            // Iterate over the sold-out rooms data
            $sold_out_room_count = 1;
            while ($row = sqlFETCHARRAY_LABEL($check_hotel_room_sold_out)):
                $itinerary_route_date = $row['route_date'];
                $location_name = $row['location_name'];

                $price_difference_hotel_sold_out_details .= '
                    <tr>
                        <td><p class="fw-medium mb-0">' . $sold_out_room_count . '</p></td>
                        <td>' . date('M d, Y', strtotime($itinerary_route_date)) . '</td>
                        <td>' . htmlspecialchars($location_name, ENT_QUOTES, 'UTF-8') . '</td>
                    </tr>';
                $sold_out_room_count++;
            endwhile;

            // Close the table
            $price_difference_hotel_sold_out_details .= '
                        </tbody>
                    </table>
                </div>';
        endif;

        // Append the details to errors if applicable
        if (!empty($price_difference_hotel_sold_out_details)):
            $errors['price_changed_details'] .= $price_difference_hotel_sold_out_details;
        endif;

        // GUIDE PRICE FOR WHOLE ITINERARY
        if ($guide_for_itinerary == 1):
            $guide_type = 1; // Whole itinerary guide
            
            // Fetch itinerary plan guide details
            $select_itinerary_plan_guide_data = sqlQUERY_LABEL("SELECT `route_guide_ID`, `itinerary_plan_ID`, `itinerary_route_ID`, `guide_id`, `guide_type`, `guide_language`, `guide_slot`, `guide_cost` FROM `dvi_itinerary_route_guide_details` WHERE `deleted` = '0' AND `itinerary_plan_ID` = '$itinerary_plan_ID' AND `guide_type` = '$guide_type'") or die("#3-UNABLE_TO_COLLECT_DETAILS: " . sqlERROR_LABEL());
            $select_itinerary_plan_guide_count = sqlNUMOFROW_LABEL($select_itinerary_plan_guide_data);
            if ($select_itinerary_plan_guide_count > 0):
                $total_guide_cost = 0; // Initialize total guide cost
                $total_guide_day_price = 0; // Initialize total guide day price

                while ($guide_row = sqlFETCHARRAY_LABEL($select_itinerary_plan_guide_data)):
                    $guide_id = $guide_row['guide_id'];
                    $guide_slots_formatted = "1,2,3";
                    $guide_cost = $guide_row['guide_cost'];

                    // Determine pax count based on total passengers
                    if ($total_pax_count <= 5):
                        $pax_count = 1;
                    elseif ($total_pax_count > 5 && $total_pax_count <= 14):
                        $pax_count = 2;
                    elseif ($total_pax_count >= 15):
                        $pax_count = 3;
                    endif;

                    $get_between_days_for_guide = getDatesBetween2Dates($trip_start_date_and_time,$trip_end_date_and_time);
                    // Loop through each day between the start and end dates
                    foreach ($get_between_days_for_guide as $days):
                        $guide_route_year = date('Y', strtotime($days));
                        $guide_route_month = date('F', strtotime($days));
                        $guide_route_day_no = date('j', strtotime($days));

                        // Fetch guide price details
                        $select_guide_price_data = sqlQUERY_LABEL("SELECT `day_$guide_route_day_no` AS GUIDE_PRICE FROM `dvi_guide_pricebook` WHERE `deleted` = '0' AND `guide_id` = '$guide_id' AND `slot_type` IN ($guide_slots_formatted) AND `year` = '$guide_route_year' AND `month` = '$guide_route_month' AND `pax_count` = '$pax_count'") or die("#3-UNABLE_TO_COLLECT_DETAILS: " . sqlERROR_LABEL());
                        $select_guide_price_count = sqlNUMOFROW_LABEL($select_guide_price_data);

                        // Sum up the guide prices
                        if ($select_guide_price_count > 0):
                            while ($guide_price_row = sqlFETCHARRAY_LABEL($select_guide_price_data)):
                                $guide_day_price = $guide_price_row["GUIDE_PRICE"];
                                $total_guide_day_price += $guide_day_price;
                            endwhile;
                        endif;
                    endforeach;
                endwhile;

                // Compare guide costs and generate price difference details
                if ($guide_cost != $total_guide_day_price):
                    $count++;
                    $price_difference_details_guide .= '
                        <div class="alert alert-warning alert-dismissible alert-label-icon label-arrow fade show" role="alert">
                            <i class="ri-alert-line label-icon"></i>
                            <strong>In this Quotation, the price updated have been changed for the Whole Itinerary Guide</strong>
                        </div>
                        <div class="table-responsive mb-3">
                            <table class="table table-hover table-bordered table-nowrap align-middle mb-0">
                                <thead>
                                    <tr class="text-muted text-uppercase">
                                        <th scope="col">S.No.</th>
                                        <th scope="col">Type</th>
                                        <th scope="col">Previous Total Rate</th>
                                        <th scope="col">New Total Rate</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    <tr>
                                        <td><p class="fw-medium mb-0">'.$count.'</p></td>
                                        <td>Guide</td>
                                        <td class="text-right">' . general_currency_symbol . ' ' . number_format($guide_cost, 2) . '</td>
                                        <td class="text-right">' . general_currency_symbol . ' ' . number_format($total_guide_day_price, 2) . '</td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>';
                endif;
            endif;
        else:
            $guide_type = 2; // Guide for individual days
        endif;

        // Display any guide price difference details if found
        if (isset($price_difference_details_guide) && $price_difference_details_guide != ''):
            $errors['price_changed_details'] .= $price_difference_details_guide;
        endif;

        //CHECK PRICE CHANGE FOR EACH DAY OF ITINERARY
        $fetch_route_details = sqlQUERY_LABEL("SELECT `itinerary_route_ID`, `itinerary_plan_ID`, `location_id`, `location_name`, `itinerary_route_date`, `no_of_days`, `no_of_km`, `direct_to_next_visiting_place`, `next_visiting_location`, `route_start_time`, `route_end_time` FROM `dvi_itinerary_route_details` WHERE `itinerary_plan_ID`='$itinerary_plan_ID' AND `deleted`='0'") or die("#1_UNABLE_TO_FETCH_DATA:" . sqlERROR_LABEL());

        if (sqlNUMOFROW_LABEL($fetch_route_details) > 0) :
            while ($route_row = sqlFETCHARRAY_LABEL($fetch_route_details)) :
                $itinerary_route_ID = $route_row['itinerary_route_ID'];
                $itinerary_route_date = $route_row['itinerary_route_date'];
                // Extract year and month from the route date
                $route_year = date('Y', strtotime($itinerary_route_date));
                $route_month = date('F', strtotime($itinerary_route_date));
                // Day number without leading zero
                $route_day_no = date('j', strtotime($itinerary_route_date));
                
                //CHECK HOTEL PRICE DETAILS
                $select_itinerary_plan_hotel_data = sqlQUERY_LABEL("SELECT `room_rate`, `hotel_id`, `room_type_id`, `room_id` FROM `dvi_itinerary_plan_hotel_room_details` WHERE `deleted` = '0' AND `itinerary_plan_id` = '$itinerary_plan_ID' AND `itinerary_route_id` = '$itinerary_route_ID' AND `group_type` = '$hotel_group_type'") or die("#3-UNABLE_TO_COLLECT_DETAILS:" . sqlERROR_LABEL());
                $select_itinerary_plan_hotel_count = sqlNUMOFROW_LABEL($select_itinerary_plan_hotel_data);
                $pd_room_count =0;
                if ($select_itinerary_plan_hotel_count > 0 && (in_array($itinerary_preference, array(1, 3)))) :
                    while ($hotel_row = sqlFETCHARRAY_LABEL($select_itinerary_plan_hotel_data)) :
                        $pd_room_count++;
                        $room_rate = $hotel_row['room_rate'];
                        $hotel_id = $hotel_row['hotel_id'];
                        $room_id = $hotel_row['room_id'];
                        $room_type_id = $hotel_row['room_type_id'];

                        $select_hotel_price_data = sqlQUERY_LABEL("SELECT `room_type_id`, `room_id`, `day_$route_day_no` FROM `dvi_hotel_room_price_book` WHERE `deleted` = '0' AND `hotel_id` = '$hotel_id' AND `room_type_id` = '$room_type_id' AND `room_id` = '$room_id' AND `year`='$route_year' AND `month`='$route_month' AND `price_type`='0'") or die("#3-UNABLE_TO_COLLECT_DETAILS:" . sqlERROR_LABEL());
                        $select_hotel_price_count = sqlNUMOFROW_LABEL($select_hotel_price_data);
                        if ($select_hotel_price_count > 0) :
                            while ($hotel_price_row = sqlFETCHARRAY_LABEL($select_hotel_price_data)) :
                                $day_price = $hotel_price_row["day_$route_day_no"];
                                $room_id = $hotel_price_row["room_id"];
                                $room_type_id = $hotel_price_row["room_type_id"];
                                if ($room_rate != $day_price) :
                                    $count++;
                                    if($price_differnce_details == ''):
                                    $price_differnce_details .= '<div class="alert alert-warning alert-dismissible alert-label-icon label-arrow fade show" role="alert">
                                    <i class="ri-alert-line label-icon"></i><strong>In this Quotation, the price updated have  been changed for Hotel/Vendor/Guide/Activity</strong></div>
                                    <span></span>
                                    <input type="hidden" name="confirm_duplicate" value="1" hidden>
                                    <div class="table-responsive">
                                        <table class="table table-hover table-bordered table-nowrap align-middle mb-0">
                                            <thead>
                                                <tr class="text-muted text-uppercase">
                                                    <th scope="col">S.No.</th>
                                                    <th scope="col">Type</th>
                                                    <th scope="col">Date </th>
                                                    <th scope="col">Previous Rate</th>
                                                    <th scope="col">New Rate</th>
                                                </tr>
                                            </thead><tbody>';
                                    endif;
                                    $price_differnce_details .=
                                        '<tr>
                                            <td><p class="fw-medium mb-0">' . $count . '</p></td>
                                            <td> Hotel '.getROOM_DETAILS($room_id,'room_title').' ['.getROOM_DETAILS($room_type_id,'ROOM_TYPE_TITLE').'] (Room #'.$pd_room_count.')</td>
                                            <td>' .  date('M d, Y',strtotime($itinerary_route_date)) . '</td>
                                            <td class="text-right">'  .  general_currency_symbol . ' ' . number_format($room_rate,2) . '</td>
                                            <td class="text-right">' . general_currency_symbol . ' ' . number_format($day_price,2) . '</td>
                                        </tr>';
                                endif;
                            endwhile;
                        endif;
                    endwhile;
                endif;

                //CHECK VENDOR VEHICLE PRICE
                $select_itinerary_plan_vendor_data = sqlQUERY_LABEL("SELECT `itinerary_plan_vendor_eligible_ID`, `itineary_plan_assigned_status`, `itinerary_plan_id`, `vehicle_type_id`, `total_vehicle_qty`, `vendor_id`, `outstation_allowed_km_per_day`, `vendor_vehicle_type_id`, `vehicle_id`, `vendor_branch_id`, `vehicle_orign`, `vehicle_count` FROM `dvi_itinerary_plan_vendor_eligible_list` WHERE `deleted` = '0' AND `itinerary_plan_id` = '$itinerary_plan_ID' AND `itineary_plan_assigned_status`='1'") or die("#3-UNABLE_TO_COLLECT_DETAILS:" . sqlERROR_LABEL());
                $select_itinerary_plan_vendor_count = sqlNUMOFROW_LABEL($select_itinerary_plan_vendor_data);
                if ($select_itinerary_plan_vendor_count > 0) :
                    while ($vendor_row = sqlFETCHARRAY_LABEL($select_itinerary_plan_vendor_data)) :
                        $itinerary_plan_vendor_eligible_ID = $vendor_row['itinerary_plan_vendor_eligible_ID'];

                        $select_itinerary_plan_vehicle_details = sqlQUERY_LABEL("SELECT `itinerary_plan_vendor_vehicle_details_ID`, `itinerary_plan_vendor_eligible_ID`, `itinerary_plan_id`, `itinerary_route_id`, `itinerary_route_date`, `vehicle_type_id`, `vendor_id`, `vendor_vehicle_type_id`, `vehicle_id`, `vendor_branch_id`,  `vehicle_rental_charges`,`travel_type`,`total_travelled_time`,`total_travelled_km` FROM `dvi_itinerary_plan_vendor_vehicle_details` WHERE `deleted` = '0' AND `itinerary_plan_id` = '$itinerary_plan_ID' AND `itinerary_route_id`='$itinerary_route_ID'  AND `itinerary_plan_vendor_eligible_ID`='$itinerary_plan_vendor_eligible_ID' GROUP BY `vendor_vehicle_type_id`") or die("#3-UNABLE_TO_COLLECT_DETAILS:" . sqlERROR_LABEL());
