                                        $select_hotel_amenities_price_data = sqlQUERY_LABEL("SELECT `hotel_amenities_price_book_id`, `hotel_id`, `hotel_amenities_id`, `pricetype`, `year`, `month`, `day_$route_day_no` FROM `dvi_hotel_amenities_price_book` WHERE `deleted` = '0' AND `hotel_id` = '$hotel_id' AND `hotel_amenities_id` = '$hotel_amenities_id' AND `year`='$route_year' AND `month`='$route_month' AND `pricetype`='1'") or die("#3-UNABLE_TO_COLLECT_DETAILS:" . sqlERROR_LABEL());
                                        $select_hotel_amenities_price_count = sqlNUMOFROW_LABEL($select_hotel_amenities_price_data);
                                        if ($select_hotel_amenities_price_count > 0) :
                                            while ($hotel_amenities_price_row = sqlFETCHARRAY_LABEL($select_hotel_amenities_price_data)) :
                                                $amenitiy_day_price = $hotel_amenities_price_row["day_$route_day_no"];
                                            endwhile;
                                        endif;

                                        if ($amenitie_rate != $amenitiy_day_price) :
                                            $amenitie_rate = $amenitiy_day_price;
                                            $total_amenitie_cost = ($amenitiy_day_price * $total_qty);
                                            $get_gst_type = get_ITINEARY_PLAN_HOTEL_ROOM_DETAILS($itinerary_plan_ID, $itinerary_route_ID, $hotel_id, '', 'get_gst_type');
                                            $get_gst_percentage = get_ITINEARY_PLAN_HOTEL_ROOM_DETAILS($itinerary_plan_ID, $itinerary_route_ID, $hotel_id, '', 'get_gst_percentage');
                                            if ($total_amenitie_cost > 0 && $get_gst_percentage > 0) :
                                                // Calculate new margin amount and room tax amount based on GST type
                                                if ($get_gst_type == 1) :
                                                    // For Inclusive GST
                                                    $new_amenitie_tax_amt = ((($total_amenitie_cost) * $get_gst_percentage) / 100);
                                                    $new_amenitie_amount = (($total_amenitie_cost) - ($new_amenitie_tax_amt));
                                                elseif ($get_gst_type == 2) :
                                                    // For Exclusive GST
                                                    $new_amenitie_tax_amt = ($total_amenitie_cost * $get_gst_percentage / 100);
                                                    $new_amenitie_amount = $total_amenitie_cost;
                                                endif;
                                            else :
                                                $new_amenitie_amount = $total_amenitie_cost;
                                                $new_amenitie_tax_amt = 0;
