                                        $arrValues_hotel_amenities = array("$itinerary_plan_hotel_room_amenities_details_ID","$itinerary_plan_hotel_details_id","$confirmed_itinerary_plan_hotel_details_id", "$group_type", "$itinerary_plan_ID", "$itinerary_route_id", "$itinerary_route_date", "$hotel_id", "$hotel_amenities_id", "$total_qty", "$amenitie_rate", "$total_amenitie_cost", "$total_amenitie_gst_amount",  "$logged_user_id", "1");
                        
