                                //UPDATE NEW PRICE OF ITINEARY HOTEL DETAILS
                                $select_itinerary_plan_hotel_details = sqlQUERY_LABEL("SELECT `itinerary_plan_hotel_details_ID`, `group_type`, `itinerary_plan_id`, `itinerary_route_id`, `itinerary_route_date`, `itinerary_route_location`, `hotel_required`, `hotel_category_id`, `hotel_id`, `hotel_margin_percentage`, `hotel_margin_gst_type`, `hotel_margin_gst_percentage`, `hotel_margin_rate`, `hotel_margin_rate_tax_amt`, `hotel_breakfast_cost`, `hotel_breakfast_cost_gst_amount`, `hotel_lunch_cost`, `hotel_lunch_cost_gst_amount`, `hotel_dinner_cost`, `hotel_dinner_cost_gst_amount`, `total_no_of_persons`, `total_hotel_meal_plan_cost`, `total_hotel_meal_plan_cost_gst_amount`, `total_extra_bed_cost`, `total_extra_bed_cost_gst_amount`, `total_childwith_bed_cost`, `total_childwith_bed_cost_gst_amount`, `total_childwithout_bed_cost`, `total_childwithout_bed_cost_gst_amount`, `total_no_of_rooms`, `total_room_cost`, `total_room_gst_amount`, `total_hotel_cost`, `total_amenities_cost`, `total_amenities_gst_amount`, `total_hotel_tax_amount` FROM `dvi_itinerary_plan_hotel_details` WHERE `deleted` = '0' and `itinerary_plan_id` = '$itinerary_plan_ID' AND `group_type`='$hotel_group_type' AND `itinerary_route_id`='$itinerary_route_ID' ") or die("#1-UNABLE_TO_COLLECT_ITINEARY_LIST:" . sqlERROR_LABEL());
                                if (sqlNUMOFROW_LABEL($select_itinerary_plan_hotel_details) > 0) :
                                    while ($fetch_itinerary_plan_hotel_details_data = sqlFETCHARRAY_LABEL($select_itinerary_plan_hotel_details)) :
                                        $itinerary_plan_hotel_details_ID = $fetch_itinerary_plan_hotel_details_data['itinerary_plan_hotel_details_ID'];
                                        $group_type = $fetch_itinerary_plan_hotel_details_data['group_type'];
                                        $itinerary_route_id = $fetch_itinerary_plan_hotel_details_data['itinerary_route_id'];
                                        $itinerary_route_date = $fetch_itinerary_plan_hotel_details_data['itinerary_route_date'];
                                        $itinerary_route_location = $fetch_itinerary_plan_hotel_details_data['itinerary_route_location'];
                                        $hotel_required = $fetch_itinerary_plan_hotel_details_data['hotel_required'];
                                        $hotel_category_id = $fetch_itinerary_plan_hotel_details_data['hotel_category_id'];
                                        $hotel_id = $fetch_itinerary_plan_hotel_details_data['hotel_id'];
                                        $hotel_margin_percentage = $fetch_itinerary_plan_hotel_details_data['hotel_margin_percentage'];
                                        $hotel_margin_gst_type = $fetch_itinerary_plan_hotel_details_data['hotel_margin_gst_type'];
