                                        $total_childwith_bed_cost_gst_amount = $fetch_itinerary_plan_hotel_details_data['total_childwith_bed_cost_gst_amount'];
                                        $total_childwithout_bed_cost = $fetch_itinerary_plan_hotel_details_data['total_childwithout_bed_cost'];
                                        $total_childwithout_bed_cost_gst_amount = $fetch_itinerary_plan_hotel_details_data['total_childwithout_bed_cost_gst_amount'];
                                        $total_no_of_rooms = $fetch_itinerary_plan_hotel_details_data['total_no_of_rooms'];

                                        //UPDATED ROOM COST
                                        $getstatus_query = sqlQUERY_LABEL("SELECT SUM(`total_room_cost`) AS TOTAL_ROOM_COST ,SUM(`total_room_gst_amount`) AS TOTAL_ROOM_COST_GST FROM `dvi_confirmed_itinerary_plan_hotel_room_details` where `itinerary_plan_id`='$itinerary_plan_ID' and `itinerary_route_id` = '$itinerary_route_ID' AND `group_type` = '$group_type' and `status` = '1' and `deleted` ='0'") or die("#getROOMTYPE_DETAILS: get_ASSIGNED_HOTEL_FOR_ITINEARY_PLAN_DETAILS: " . sqlERROR_LABEL());
                                        while ($getstatus_fetch = sqlFETCHARRAY_LABEL($getstatus_query)) :
                                            $total_room_cost = $getstatus_fetch['TOTAL_ROOM_COST'];
                                            $total_room_gst_amount = $getstatus_fetch['TOTAL_ROOM_COST_GST'];
                                        endwhile;
                                        
                                        //UPDATED AMENITIES COST
                                        $total_amenities_cost = get_CONFIRMED_ITINEARYHOTEL_AMENITIES_DETAILS($hotel_group_type, $itinerary_plan_ID, $itinerary_route_ID, 'TOTAL_AMENITIES_COST');
                                        $total_amenities_gst_amount = get_CONFIRMED_ITINEARYHOTEL_AMENITIES_DETAILS($hotel_group_type, $itinerary_plan_ID, $itinerary_route_ID, 'TOTAL_AMENITIES_GST_COST');

                                        $total_new_room_cost = $total_room_cost + $hotel_breakfast_cost + $hotel_lunch_cost + $hotel_dinner_cost + $total_extra_bed_cost + $total_childwithout_bed_cost + $total_childwith_bed_cost + $total_amenities_cost;

                                        if ($hotel_margin_percentage > 0 && $total_new_room_cost > 0) :
