                                        $arrFields_hotel = array('`itinerary_plan_hotel_details_ID`', '`group_type`', '`itinerary_plan_id`', '`itinerary_route_id`', '`itinerary_route_date`', '`itinerary_route_location`', '`hotel_required`', '`hotel_category_id`', '`hotel_id`', '`hotel_margin_percentage`', '`hotel_margin_gst_type`', '`hotel_margin_gst_percentage`', '`hotel_margin_rate`', '`hotel_margin_rate_tax_amt`', '`hotel_breakfast_cost`', '`hotel_breakfast_cost_gst_amount`', '`hotel_lunch_cost`', '`hotel_lunch_cost_gst_amount`', '`hotel_dinner_cost`', '`hotel_dinner_cost_gst_amount`', '`total_no_of_persons`', '`total_hotel_meal_plan_cost`', '`total_hotel_meal_plan_cost_gst_amount`', '`total_extra_bed_cost`', '`total_extra_bed_cost_gst_amount`', '`total_childwith_bed_cost`', '`total_childwith_bed_cost_gst_amount`', '`total_childwithout_bed_cost`', '`total_childwithout_bed_cost_gst_amount`', '`total_no_of_rooms`', '`total_room_cost`', '`total_room_gst_amount`', '`total_hotel_cost`', '`total_amenities_cost`', '`total_amenities_gst_amount`', '`total_hotel_tax_amount`', '`createdby`', '`status`');

                                        $arrValues_hotel = array("$itinerary_plan_hotel_details_ID", "$group_type", "$itinerary_plan_ID", "$itinerary_route_id", "$itinerary_route_date", "$itinerary_route_location", "$hotel_required", "$hotel_category_id", "$hotel_id", "$hotel_margin_percentage", "$hotel_margin_gst_type", "$hotel_margin_gst_percentage", "$hotel_margin_rate", "$hotel_margin_rate_tax_amt", "$hotel_breakfast_cost", "$hotel_breakfast_cost_gst_amount", "$hotel_lunch_cost", "$hotel_lunch_cost_gst_amount", "$hotel_dinner_cost", "$hotel_dinner_cost_gst_amount", "$total_no_of_persons", "$total_hotel_meal_plan_cost", "$total_hotel_meal_plan_cost_gst_amount", "$total_extra_bed_cost", "$total_extra_bed_cost_gst_amount", "$total_childwith_bed_cost", "$total_childwith_bed_cost_gst_amount", "$total_childwithout_bed_cost", "$total_childwithout_bed_cost_gst_amount", "$total_no_of_rooms", "$total_room_cost", "$total_room_gst_amount", "$total_hotel_cost", "$total_amenities_cost", "$total_amenities_gst_amount", "$total_hotel_tax_amount", "$logged_user_id", "1");
                    
                                        if (sqlACTIONS("INSERT", "dvi_confirmed_itinerary_plan_hotel_details", $arrFields_hotel, $arrValues_hotel, '')) :
