                                            $arrValues_hotel_room_details = array("$confirmed_itinerary_plan_hotel_details_id");
                                            $sqlWhere_hotel_room_details = " `itinerary_plan_hotel_details_id` = '$itinerary_plan_hotel_details_ID' ";
                                            if (sqlACTIONS("UPDATE", "dvi_confirmed_itinerary_plan_hotel_room_details", $arrFields_hotel_room_details, $arrValues_hotel_room_details, $sqlWhere_hotel_room_details)) :
