                                //UPDATE ITINEARY GUIDE NEW PRICE FOR EACH ROUTE
                                if($guide_for_itinerary == 0):
                                    $select_itinerary_route_guide_details = sqlQUERY_LABEL("SELECT `route_guide_ID`, `itinerary_plan_ID`, `itinerary_route_ID`, `guide_id`, `guide_type`, `guide_language`, `guide_slot`, `guide_cost` FROM `dvi_itinerary_route_guide_details` WHERE `deleted` = '0' and `itinerary_plan_ID` = '$itinerary_plan_ID' AND `itinerary_route_ID` = '$itinerary_route_ID'") or die("#1-UNABLE_TO_COLLECT_ITINERARY_GUIDE_LIST:" . sqlERROR_LABEL());
                                    if (sqlNUMOFROW_LABEL($select_itinerary_route_guide_details) > 0) :
                                        while ($fetch_guide_details = sqlFETCHARRAY_LABEL($select_itinerary_route_guide_details)) :
                                            $route_guide_ID = $fetch_guide_details['route_guide_ID'];
                                            $itinerary_plan_ID = $fetch_guide_details['itinerary_plan_ID'];
                                            $itinerary_route_ID = $fetch_guide_details['itinerary_route_ID'];
                                            $guide_id = $fetch_guide_details['guide_id'];
                                            $guide_type = $fetch_guide_details['guide_type'];
                                            $guide_language = $fetch_guide_details['guide_language'];
                                            $guide_slot = $fetch_guide_details['guide_slot'];
                                            $guide_cost = $fetch_guide_details['guide_cost'];

                                            // Explode the guide_slot string into an array, wrap each element in quotes, then implode it back into a string
                                            $guide_slots_array = explode(',', $guide_slot);
                                            $guide_slots_quoted = array_map(function($slot) {
                                                return "'" . trim($slot) . "'";
                                            }, $guide_slots_array);
                                            $guide_slots_formatted = implode(',', $guide_slots_quoted);
                                            
                                            if ($total_pax_count <= 5) :
                                                $pax_count = 1;
                                            elseif ($total_pax_count > 5 && $total_pax_count <= 14) :
                                                $pax_count = 2;
                                            elseif ($total_pax_count >= 15) :
                                                $pax_count = 3;
                                            endif;

                                            $select_guide_price_data = sqlQUERY_LABEL("SELECT  `guide_price_book_ID`, `guide_id`, `year`, `month`, `pax_count`, `slot_type`, `year`, `month`, SUM(`day_$route_day_no`) AS GUIDE_PRICE FROM `dvi_guide_pricebook` WHERE `deleted` = '0' AND `guide_id` = '$guide_id' AND (`slot_type` IN ($guide_slots_formatted) ) AND `year`='$route_year' AND `month`='$route_month' AND `pax_count`='$pax_count' ") or die("#3-UNABLE_TO_COLLECT_DETAILS:" . sqlERROR_LABEL());
                                            $select_guide_price_count = sqlNUMOFROW_LABEL($select_guide_price_data);
