                                    //INSERT ITINEARY GUIDE SLOT COST DETAILS
                                    $select_itinerary_route_guide_slot_cost_details = sqlQUERY_LABEL("SELECT `guide_slot_cost_details_id`, `route_guide_id`, `itinerary_plan_id`, `itinerary_route_id`, `itinerary_route_date`, `guide_id`, `guide_type`, `guide_slot`, `guide_slot_cost` FROM `dvi_itinerary_route_guide_slot_cost_details` WHERE `deleted` = '0' and `itinerary_plan_ID` = '$itinerary_plan_ID'") or die("#1-UNABLE_TO_COLLECT_ITINERARY_GUIDE_LIST:" . sqlERROR_LABEL());
                                    if (sqlNUMOFROW_LABEL($select_itinerary_route_guide_slot_cost_details) > 0) :
