                                endif;

                                // FETCH AND INSERT INTO `dvi_confirmed_itinerary_route_activity_details`
                                $select_itinerary_route_activity_details = sqlQUERY_LABEL("SELECT `route_activity_ID`, `itinerary_plan_ID`, `itinerary_route_ID`, `route_hotspot_ID`, `hotspot_ID`, `activity_ID`, `activity_order`, `activity_charges_for_foreign_adult`, `activity_charges_for_foreign_children`, `activity_charges_for_foreign_infant`, `activity_charges_for_adult`, `activity_charges_for_children`, `activity_charges_for_infant`, `activity_amout`, `activity_traveling_time`, `activity_start_time`, `activity_end_time` FROM `dvi_itinerary_route_activity_details` WHERE `deleted` = '0' AND `itinerary_plan_ID` = '$itinerary_plan_ID' AND `itinerary_route_ID` = '$itinerary_route_ID'") or die("#1-UNABLE_TO_COLLECT_ACTIVITY_LIST:" . sqlERROR_LABEL());
                                if (sqlNUMOFROW_LABEL($select_itinerary_route_activity_details) > 0) :
