                                //UPDATE ITINEARY VENDOR VEHICLE DETAILS FOR EACH ROUTE
                                $select_itinerary_plan_vendor_vehicle_details = sqlQUERY_LABEL("SELECT vendor_vehicle.`itinerary_plan_vendor_vehicle_details_ID`, vendor_vehicle.`itinerary_plan_vendor_eligible_ID`, vendor_vehicle.`itinerary_plan_id`, vendor_vehicle.`itinerary_route_id`, vendor_vehicle.`itinerary_route_date`, vendor_vehicle.`vehicle_type_id`, vendor_vehicle.`vendor_id`, vendor_vehicle.`vendor_vehicle_type_id`, vendor_vehicle.`vehicle_id`, vendor_vehicle.`vendor_branch_id`,  vendor_vehicle.`vehicle_rental_charges`,vendor_vehicle.`travel_type`,vendor_vehicle.`total_travelled_time`,vendor_vehicle.`total_travelled_km` , vendor_vehicle.`itinerary_route_location_from`, vendor_vehicle.`itinerary_route_location_to`, vendor_vehicle.`total_running_km`, vendor_vehicle.`total_running_time`, vendor_vehicle.`total_siteseeing_km`, vendor_vehicle.`total_siteseeing_time`, vendor_vehicle.`total_pickup_km`, vendor_vehicle.`total_pickup_duration`, vendor_vehicle.`total_drop_km`, vendor_vehicle.`total_drop_duration`,  vendor_vehicle.`total_extra_km`,  vendor_vehicle.`extra_km_rate`,  vendor_vehicle.`total_extra_km_charges`,  vendor_vehicle.`vehicle_rental_charges`, vendor_vehicle.`vehicle_toll_charges`, vendor_vehicle.`vehicle_parking_charges`, vendor_vehicle.`vehicle_driver_charges`, vendor_vehicle.`vehicle_permit_charges`, vendor_vehicle.`before_6_am_extra_time`, vendor_vehicle.`after_8_pm_extra_time`, vendor_vehicle.`before_6_am_charges_for_driver`, vendor_vehicle.`before_6_am_charges_for_vehicle`, vendor_vehicle.`after_8_pm_charges_for_driver`, vendor_vehicle.`after_8_pm_charges_for_vehicle`, vendor_vehicle.`total_vehicle_amount` FROM `dvi_itinerary_plan_vendor_vehicle_details` vendor_vehicle INNER JOIN `dvi_itinerary_plan_vendor_eligible_list` eligible_list ON vendor_vehicle.`itinerary_plan_vendor_eligible_ID` = eligible_list.`itinerary_plan_vendor_eligible_ID` WHERE vendor_vehicle.`deleted` = '0' AND vendor_vehicle.`itinerary_plan_id` = '$itinerary_plan_ID' AND vendor_vehicle.`itinerary_route_id` = '$itinerary_route_ID' AND eligible_list.`deleted` = '0' AND eligible_list.`itinerary_plan_id` = '$itinerary_plan_ID' AND eligible_list.`itineary_plan_assigned_status` = '1'") or die("#1-UNABLE_TO_COLLECT_ITINEARY_LIST:" . sqlERROR_LABEL());
                                if (sqlNUMOFROW_LABEL($select_itinerary_plan_vendor_vehicle_details) > 0) :
                                    while ($fetch_vehicle_details = sqlFETCHARRAY_LABEL($select_itinerary_plan_vendor_vehicle_details)):
                                        $itinerary_plan_vendor_vehicle_details_ID = $fetch_vehicle_details['itinerary_plan_vendor_vehicle_details_ID'];
                                        $itinerary_plan_vendor_eligible_ID = $fetch_vehicle_details['itinerary_plan_vendor_eligible_ID'];
                                        $itinerary_route_id = $fetch_vehicle_details['itinerary_route_id'];
                                        $itinerary_route_date = $fetch_vehicle_details['itinerary_route_date'];
                                        $vehicle_type_id = $fetch_vehicle_details['vehicle_type_id'];
                                        $vendor_id = $fetch_vehicle_details['vendor_id'];
                                        $vendor_vehicle_type_id = $fetch_vehicle_details['vendor_vehicle_type_id'];
                                        $vehicle_id = $fetch_vehicle_details['vehicle_id'];
                                        $vendor_branch_id = $fetch_vehicle_details['vendor_branch_id'];
                                        $travel_type = $fetch_vehicle_details['travel_type'];
                                        $itinerary_route_location_from = $fetch_vehicle_details['itinerary_route_location_from'];
                                        $itinerary_route_location_to = $fetch_vehicle_details['itinerary_route_location_to'];
                                        $total_running_km = $fetch_vehicle_details['total_running_km'];
                                        $total_running_time = $fetch_vehicle_details['total_running_time'];
                                        $total_siteseeing_km = $fetch_vehicle_details['total_siteseeing_km'];
                                        $total_siteseeing_time = $fetch_vehicle_details['total_siteseeing_time'];
                                        $total_pickup_km = $fetch_vehicle_details['total_pickup_km'];
                                        $total_pickup_duration = $fetch_vehicle_details['total_pickup_duration'];
                                        $total_drop_km = $fetch_vehicle_details['total_drop_km'];
                                        $total_drop_duration = $fetch_vehicle_details['total_drop_duration'];
                                        $total_extra_km = $fetch_vehicle_details['total_extra_km'];
                                        $extra_km_rate = $fetch_vehicle_details['extra_km_rate'];
                                        $total_extra_km_charges = $fetch_vehicle_details['total_extra_km_charges'];
                                        $total_travelled_km = $fetch_vehicle_details['total_travelled_km'];
                                        $total_travelled_time = $fetch_vehicle_details['total_travelled_time'];
                                        $vehicle_rental_charges = $fetch_vehicle_details['vehicle_rental_charges'];
                                        $vehicle_toll_charges = $fetch_vehicle_details['vehicle_toll_charges'];
                                        $vehicle_parking_charges = $fetch_vehicle_details['vehicle_parking_charges'];
                                        $vehicle_driver_charges = $fetch_vehicle_details['vehicle_driver_charges'];
                                        $vehicle_permit_charges = $fetch_vehicle_details['vehicle_permit_charges'];
                                        $before_6_am_extra_time = $fetch_vehicle_details['before_6_am_extra_time'];
                                        $after_8_pm_extra_time = $fetch_vehicle_details['after_8_pm_extra_time'];
                                        $before_6_am_charges_for_driver = $fetch_vehicle_details['before_6_am_charges_for_driver'];
                                        $before_6_am_charges_for_vehicle = $fetch_vehicle_details['before_6_am_charges_for_vehicle'];
                                        $after_8_pm_charges_for_driver = $fetch_vehicle_details['after_8_pm_charges_for_driver'];
                                        $after_8_pm_charges_for_vehicle = $fetch_vehicle_details['after_8_pm_charges_for_vehicle'];
                                        $total_vehicle_amount = $fetch_vehicle_details['total_vehicle_amount'];
                                        $TOTAL_KM = $vehicle_row['total_travelled_km'];

                                        if ($travel_type == 1) : 
                                            //Local
                                            // Convert time durations to seconds
                                            $total_travelled_time_IN_SECONDS = strtotime($total_travelled_time) - strtotime('00:00:00');
                                            $TOTAL_TIME = gmdate('H:i:s', $total_travelled_time_IN_SECONDS);
                                            $time_parts = explode(':', $TOTAL_TIME);
                                            $TOTAL_TIME_hours = intval($time_parts[0]);
                                            $TOTAL_TIME_minutes = intval($time_parts[1]);

                                            // Round the total time based on minutes
                                            if ($TOTAL_TIME_minutes < 30) :
                                                $TOTAL_HOURS =  $TOTAL_TIME_hours;
                                            else :
                                                $TOTAL_HOURS = $TOTAL_TIME_hours + 1;
                                            endif;
                                                
                                            $hours_time_limit_id = getTIMELIMIT($vendor_vehicle_type_id, 'get_time_limit_id_from_hour_limit', $vendor_id, $TOTAL_HOURS);

                                            $km_time_limit_id = getTIMELIMIT($vendor_vehicle_type_ID, 'get_time_limit_id_from_km_limit', $vendor_id, $TOTAL_HOURS, $TOTAL_KM);

                                            if ($km_time_limit_id == $hours_time_limit_id) :
                                                $time_limit_id = $km_time_limit_id;
                                            elseif ($km_time_limit_id > $hours_time_limit_id) :
                                                //IF KM IS GREATER
                                                $time_limit_id = $km_time_limit_id;
                                            elseif ($km_time_limit_id < $hours_time_limit_id) :
                                                //IF TIME IS GREATER
                                                $time_limit_id = $hours_time_limit_id;
                                            endif;

                                            $select_vehicle_price_data = sqlQUERY_LABEL("SELECT `day_$route_day_no` FROM `dvi_vehicle_local_pricebook` WHERE `deleted` = '0' AND `vendor_id` = '$vendor_id' AND `vendor_branch_id` = '$vendor_branch_id' AND `vehicle_type_id` = '$vendor_vehicle_type_id' AND `year`='$route_year' AND `month`='$route_month' AND `time_limit_id`='$time_limit_id'") or die("#3-UNABLE_TO_COLLECT_DETAILS:" . sqlERROR_LABEL());
                                            $select_vehicle_price_count = sqlNUMOFROW_LABEL($select_vehicle_price_data);
                                            if ($select_vehicle_price_count > 0) :
                                                while ($vehicle_price_row = sqlFETCHARRAY_LABEL($select_vehicle_price_data)) :
                                                    $vehicle_day_price = $vehicle_price_row["day_$route_day_no"];
                                                endwhile;
                                            endif;

                                            if ($vehicle_rental_charges != $vehicle_day_price) :
                                                $vehicle_rental_charges = $vehicle_day_price;    
                                                $total_vehicle_amount = ($vehicle_rental_charges + $vehicle_toll_charges + $vehicle_parking_charges + $vehicle_driver_charges + $vehicle_permit_charges + $before_6_am_charges_for_driver + $before_6_am_charges_for_vehicle + $after_8_pm_charges_for_driver + $after_8_pm_charges_for_vehicle);   
                                            endif;

                                        elseif ($travel_type == 2) :
                                            //outstation
                                            $select_vehicle_price_data = sqlQUERY_LABEL("SELECT `day_$route_day_no` FROM `dvi_vehicle_outstation_price_book` WHERE `deleted` = '0' AND `vendor_id` = '$vendor_id' AND `vendor_branch_id` = '$vendor_branch_id' AND `vehicle_type_id` = '$vendor_vehicle_type_id' AND `year`='$route_year' AND `month`='$route_month' ") or die("#3-UNABLE_TO_COLLECT_DETAILS:" . sqlERROR_LABEL());
                                            $select_vehicle_price_count = sqlNUMOFROW_LABEL($select_vehicle_price_data);
                                            if ($select_vehicle_price_count > 0) :
                                                while ($vehicle_price_row = sqlFETCHARRAY_LABEL($select_vehicle_price_data)) :
                                                    $count++;
                                                    //FETCH THE PRICE OF ROUTE DATE
                                                    $vehicle_day_price = $vehicle_price_row["day_$route_day_no"];
                                                endwhile;
                                            endif;

                                            if ($vehicle_rental_charges != $vehicle_day_price) :
                                                $vehicle_rental_charges = $vehicle_day_price;    
