                                            endif;

                                        endif;

                                        $arrFields_vehicle_details = array('`itinerary_plan_vendor_vehicle_details_ID`', '`itinerary_plan_vendor_eligible_ID`', '`itinerary_plan_id`', '`itinerary_route_id`', '`itinerary_route_date`', '`vehicle_type_id`','`vehicle_qty`', '`vendor_id`', '`vendor_vehicle_type_id`', '`vehicle_id`', '`vendor_branch_id`', '`travel_type`', '`itinerary_route_location_from`', '`itinerary_route_location_to`', '`total_running_km`', '`total_running_time`', '`total_siteseeing_km`', '`total_siteseeing_time`', '`total_pickup_km`', '`total_pickup_duration`', '`total_drop_km`', '`total_drop_duration`', '`total_extra_km`', '`extra_km_rate`', '`total_extra_km_charges`', '`total_travelled_km`', '`total_travelled_time`', '`vehicle_rental_charges`', '`vehicle_toll_charges`', '`vehicle_parking_charges`', '`vehicle_driver_charges`', '`vehicle_permit_charges`', '`before_6_am_extra_time`', '`after_8_pm_extra_time`', '`before_6_am_charges_for_driver`', '`before_6_am_charges_for_vehicle`', '`after_8_pm_charges_for_driver`', '`after_8_pm_charges_for_vehicle`', '`total_vehicle_amount`', '`createdby`', '`status`');

                                        $arrValues_vehicle_details = array( "$itinerary_plan_vendor_vehicle_details_ID", "$itinerary_plan_vendor_eligible_ID", "$itinerary_plan_ID", "$itinerary_route_id", "$itinerary_route_date", "$vehicle_type_id","1", "$vendor_id", "$vendor_vehicle_type_id", "$vehicle_id", "$vendor_branch_id", "$travel_type", "$itinerary_route_location_from", "$itinerary_route_location_to", "$total_running_km", "$total_running_time", "$total_siteseeing_km", "$total_siteseeing_time", "$total_pickup_km", "$total_pickup_duration", "$total_drop_km", "$total_drop_duration", "$total_extra_km","$extra_km_rate","$total_extra_km_charges","$total_travelled_km", "$total_travelled_time", "$vehicle_rental_charges", "$vehicle_toll_charges", "$vehicle_parking_charges", "$vehicle_driver_charges", "$vehicle_permit_charges", "$before_6_am_extra_time", "$after_8_pm_extra_time", "$before_6_am_charges_for_driver", "$before_6_am_charges_for_vehicle", "$after_8_pm_charges_for_driver", "$after_8_pm_charges_for_vehicle", "$total_vehicle_amount", "$logged_user_id", "1");

                                        if (sqlACTIONS("INSERT", "dvi_confirmed_itinerary_plan_vendor_vehicle_details", $arrFields_vehicle_details, $arrValues_vehicle_details, '')) :
                                        endif;
                                    endwhile;
                                endif;
                            endwhile;
                        endif;
                        //END OF ROUTE

                        //UPDATE VENDOR ELIGIBILITY BASED ON NEW PRICE UPDATED IN EACH ROUTE TO UNASSIGNED
                        $fetch_eligibility_details = sqlQUERY_LABEL("SELECT DISTINCT e.vendor_vehicle_type_id, SUM(v.vehicle_rental_charges) AS total_rental_charges, e.itinerary_plan_vendor_eligible_ID, SUM(v.total_vehicle_amount) AS vehicle_total_amount, e.vehicle_gst_type, e.vehicle_gst_percentage, e.vendor_margin_percentage, e.vendor_margin_gst_type, e.vendor_margin_gst_percentage FROM dvi_confirmed_itinerary_plan_vendor_eligible_list e JOIN dvi_confirmed_itinerary_plan_vendor_vehicle_details v ON e.itinerary_plan_vendor_eligible_ID = v.itinerary_plan_vendor_eligible_ID WHERE e.`itinerary_plan_id` = '$itinerary_plan_ID' AND e.itineary_plan_assigned_status = '1' AND e.`deleted` = '0' GROUP BY e.vendor_vehicle_type_id, e.itinerary_plan_vendor_eligible_ID ORDER BY e.vendor_vehicle_type_id") or die("#1_UNABLE_TO_FETCH_DATA:" . sqlERROR_LABEL());
                        if (sqlNUMOFROW_LABEL($fetch_eligibility_details) > 0) :
                            while ($fetch_eligibility_row = sqlFETCHARRAY_LABEL($fetch_eligibility_details)) :
                                $itinerary_plan_vendor_eligible_ID = $fetch_eligibility_row['itinerary_plan_vendor_eligible_ID'];
                                $vendor_vehicle_type_id = $fetch_eligibility_row['vendor_vehicle_type_id'];
                                $total_rental_charges = $fetch_eligibility_row['total_rental_charges'];
                                $vehicle_total_amount = $fetch_eligibility_row['vehicle_total_amount'];
                                $vehicle_gst_type = $fetch_eligibility_row['vehicle_gst_type'];
                                $vehicle_gst_percentage = $fetch_eligibility_row['vehicle_gst_percentage'];
                                $vendor_margin_percentage = $fetch_eligibility_row['vendor_margin_percentage'];
                                $vendor_margin_gst_type = $fetch_eligibility_row['vendor_margin_gst_type'];
                                $vendor_margin_gst_percentage = $fetch_eligibility_row['vendor_margin_gst_percentage'];

                                $total_extra_kms_charge = get_LATEST_ITINERARY_VEHICLE_ELIGIBILITY_LIST_DETAILS($itinerary_plan_vendor_eligible_ID, $itinerary_plan_ID,'total_extra_kms_charge');
                                $total_extra_local_kms_charge = get_LATEST_ITINERARY_VEHICLE_ELIGIBILITY_LIST_DETAILS($itinerary_plan_vendor_eligible_ID, $itinerary_plan_ID,'total_extra_local_kms_charge');

                                $OVERALL_TOTAL_VEHICLE_AMOUNT = $vehicle_total_amount+$total_extra_kms_charge+$total_extra_local_kms_charge;

                                if ($vehicle_gst_percentage > 0) :
                                    if ($vehicle_gst_type == 1) :
                                        // For Inclusive GST
                                        $new_total_vehicle_cost = $OVERALL_TOTAL_VEHICLE_AMOUNT / (1 + ($vehicle_gst_percentage / 100));
                                        $new_total_vehicle_tax_amt = ($OVERALL_TOTAL_VEHICLE_AMOUNT - $new_total_vehicle_cost);
                                    elseif ($vehicle_gst_type == 2) :
                                        // For Exclusive GST
                                        $new_total_vehicle_cost = $OVERALL_TOTAL_VEHICLE_AMOUNT;
                                        $new_total_vehicle_tax_amt = ($OVERALL_TOTAL_VEHICLE_AMOUNT * $vehicle_gst_percentage / 100);
                                    endif;
                                else :
                                    $new_total_vehicle_cost = $OVERALL_TOTAL_VEHICLE_AMOUNT;
                                    $new_total_vehicle_tax_amt = 0;
                                endif;

                                $TOTAL_VENDOR_MARGIN_AMOUNT = (($OVERALL_TOTAL_VEHICLE_AMOUNT * $vendor_margin_percentage) / 100);

                                if ($vendor_margin_gst_percentage > 0) :
                                    if ($vendor_margin_gst_type == 1) :
                                        // For Inclusive GST
                                        $new_total_margin_amount = $TOTAL_VENDOR_MARGIN_AMOUNT / (1 + ($vendor_margin_gst_percentage / 100));
                                        $new_total_margin_service_tax_amt = ($TOTAL_VENDOR_MARGIN_AMOUNT - $new_total_margin_amount);
                                    elseif ($vendor_margin_gst_type == 2) :
                                        // For Exclusive GST
                                        $new_total_margin_amount = $TOTAL_VENDOR_MARGIN_AMOUNT;
                                        $new_total_margin_service_tax_amt = ($TOTAL_VENDOR_MARGIN_AMOUNT * $vendor_margin_gst_percentage / 100);
                                    endif;
                                else :
                                    $new_total_margin_amount = $TOTAL_VENDOR_MARGIN_AMOUNT;
                                    $new_total_margin_service_tax_amt = 0;
                                endif;

                                $VEHICLE_GRAND_TOTAL_AMOUNT = ($new_total_vehicle_cost + $new_total_vehicle_tax_amt + $new_total_margin_amount + $new_total_margin_service_tax_amt);

                                $vendor_eligible_list_arrFields = array('`total_rental_charges`','`vehicle_gst_amount`', '`vehicle_total_amount`',  '`vendor_margin_amount`', '`vendor_margin_gst_amount`', '`vehicle_grand_total`');

                                $vendor_eligible_list_arrValues = array("$total_rental_charges","$new_total_vehicle_tax_amt", "$new_total_vehicle_cost", "$new_total_margin_amount", "$new_total_margin_service_tax_amt", "$VEHICLE_GRAND_TOTAL_AMOUNT");

                                $vendor_eligible_list_sqlwhere = " `itinerary_plan_id` = '$itinerary_plan_ID' AND `vendor_vehicle_type_id` = '$vendor_vehicle_type_id' AND `itinerary_plan_vendor_eligible_ID` = '$itinerary_plan_vendor_eligible_ID' ";

